          a { @include side-radius($default-float, $global-radius); }
        }

        dd:last-child,
        .tab:last-child {
          a { @include side-radius($opposite-direction, $global-radius); }
        }
      }

      &.vertical {
        dd,
        .tab-title {
          position: inherit;
          float: none;
          display: block;
          top: auto;
        }
      }
    }

    .tabs-content {
      @include clearfix;
      margin-bottom: $tabs-content-margin-bottom;
      width: 100%;

      > .content {
        display: none;
        float: $default-float;
        padding: $tabs-content-padding 0;
        width: 100%;

        &.active {
          display: block;
          float: none;
        }
        &.contained {
          padding: $tabs-content-padding;
        }
      }

      &.vertical {
        display: block;

        > .content {
          padding: 0 $tabs-content-padding;
        }
      }
    }

    @media #{$medium-up} {
      .tabs {
        &.vertical {
          float: $default-float;
          margin: 0;
          margin-bottom: $tabs-vertical-navigation-margin-bottom !important;
          max-width: 20%;
          width: 20%;
        }
      }

      .tabs-content {
        &.vertical {
          float: $default-float;
          margin-#{$default-float}: -1px;
          max-width: 80%;
          padding-#{$default-float}: 1rem;
          width: 80%;
        }
      }
    }

    .no-js {
      .tabs-content > .content {
        display: block;
        float: none;
      }
    }
  }
}

