bootstrap/mixins/grid-framework.scss:
// Framework grid generation
//
// Used only by Bootstrap to generate the correct number of grid classes given
// any value of `$grid-columns`.

// [converter] This is defined recursively in LESS, but Sass supports real loops
@mixin make-grid-columns($i: 1, $list: ".col-xs-#{$i}, .col-sm-#{$i}, .col-md-#{$i}, .col-lg-#{$i}") {
  @for $i from (1 + 1) through $grid-columns {
    $list: "#{$list}, .col-xs-#{$i}, .col-sm-#{$i}, .col-md-#{$i}, .col-lg-#{$i}";
  }
  #{$list} {
    position: relative;
    // Prevent columns from collapsing when empty
    min-height: 1px;
    // Inner gutter via padding
    padding-left:  ceil(($grid-gutter-width / 2));
    padding-right: floor(($grid-gutter-width / 2));
  }
}


// [converter] This is defined recursively in LESS, but Sass supports real loops
@mixin float-grid-columns($class, $i: 1, $list: ".col-#{$class}-#{$i}") {
  @for $i from (1 + 1) through $grid-columns {
    $list: "#{$list}, .col-#{$class}-#{$i}";
  }
  #{$list} {
    float: left;
  }
}


@mixin calc-grid-column($index, $class, $type) {
  @if ($type == width) and ($index > 0) {
    .col-#{$class}-#{$index} {
      width: percentage(($index / $grid-columns));
    }
  }
  @if ($type == push) and ($index > 0) {
    .col-#{$class}-push-#{$index} {
      left: percentage(($index / $grid-columns));
    }
  }
  @if ($type == push) and ($index == 0) {
    .col-#{$class}-push-0 {
      left: auto;
    }
  }
  @if ($type == pull) and ($index > 0) {
    .col-#{$class}-pull-#{$index} {
      right: percentage(($index / $grid-columns));
    }
  }
  @if ($type == pull) and ($index == 0) {
    .col-#{$class}-pull-0 {
      right: auto;
    }
  }
  @if ($type == offset) {
    .col-#{$class}-offset-#{$index} {
      margin-left: percentage(($index / $grid-columns));
    }
  }
}

// [converter] This is defined recursively in LESS, but Sass supports real loops
@mixin loop-grid-columns($columns, $class, $type) {
  @for $i from 0 through $columns {
    @include calc-grid-column($i, $class, $type);
  }
}


// Create grid for specific class
@mixin make-grid($class) {
  @include float-grid-columns($class);
  @include loop-grid-columns($grid-columns, $class, width);
  @include loop-grid-columns($grid-columns, $class, pull);
  @include loop-grid-columns($grid-columns, $class, push);
  @include loop-grid-columns($grid-columns, $class, offset);
}

layouts/blog/blog.scss:
pad-content// =============================================================================
// BLOG
// =============================================================================


// Blog post
// -----------------------------------------------------------------------------

.blog {
    margin-bottom: spacing("single");
    padding: spacing("double");
    padding-top: 0;
    @include breakpoint("medium") {
        margin: auto;
        width: 100%;
    }
    + .blog {
        border-top: container("border");
        padding-top: spacing("double");
        .blog-title {
            padding-top: 0;
        }
    }

    .tags {
        margin-top: spacing("double");
        text-align: left;
    }

    .socialLinks {
        text-align: left;
    }

    // scss-lint:disable SelectorFormat
    .addthis_toolbox {
        margin-top: spacing("double");
    }
    // scss-lint:enable SelectorFormat

    blockquote, blockquote p {
        font-size: 12px;
        line-height: 18px;
        padding-left: 20px;
        margin-bottom: 20px;
    }
    .blog-post{
        h1, h2, h3, h4, h5, h6 {
            position: relative;
            padding-left: 16px;
            &:before{
                content: "\f054";
                color: #ea8e0a;
                position: absolute;
                left: 0;
                top: 4px;
                font: normal normal normal 9px/1 FontAwesome;
            }
        }
    }
}


.blog-title {
    margin: 0 0 spacing("base") 0;
    font-family: $fontFamily-headings;
    font-weight: 700;
    padding: 15px 0px 16px;
    margin-bottom: 0;
    font-size: 20px;
    @media (max-width: 480px) {
        font-size: 20px;
    }
    a {
        text-decoration: none;        
        color: $anchor-font-color-hover;
        &:hover{
            color: $anchor-font-color;
        }

    }
    
}
.brg-content{
    -moz-box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.08);
    -webkit-box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.08);
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.08);
    background-color: #ffffff;    
    border-bottom: 1px solid #dcdcdc;
    border-radius: 3px;
    overflow: hidden;
}
.page-type-blog .brg-content {
    padding: 20px;
}
.pad-content{
   padding: 30px;

}
.page-type-search {
    .brg-content{
        .page-content{
            padding: 30px;
        }
        & .pad-content {
            @media (max-width: 767px) {
                padding: 30px 15px;
            }
            @media (max-width: 550px) {
                padding: 15px;
            }
        }
    }
}
.page-type-blog_post {  
    .brg-content{
        .blog{
            padding: 30px;
        }
    }
    .blog-title{
        padding-top: 0;
        margin-bottom: -3px;
        font-size: 20px;
    }
    h1, h2, h3, h4, h5, h6 {
        font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
        font-weight: 600;
        font-size: 13px;
        letter-spacing: 0;
        line-height: 20px;
        text-transform: inherit;
    }
}
.blog-date,
.blog-author {
    color: stencilColor("color-textSecondary");
    text-align: left;
}
.blog-date {
    margin-bottom: 26px;
}
.blog-author {
    margin-bottom: spacing("single") + spacing("base");
}

.blog-post {
    font-size: fontSize("base");
    text-align: left;
    margin-bottom: 20px;
}

.blog-thumbnail {
    margin: 0 0 (spacing("base") * 2);
    margin-bottom: 36px;
    text-align: center;
}

.blog-post-figure {
    display: block;
    margin: auto;
    text-align: left;

}

.blog-post-body {
    display: block;
    margin: 0 auto;
    text-align: left;
    width: 100%;

}

.blog-readmore {
    border-width: 1px;
    font-size: 12px;
    letter-spacing: 0;
    margin-bottom: 20px;
    font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
    color: $anchor-font-color;
    height: 40px;
    font-weight: 700;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.15);
    &:hover {
        color: $anchor-font-color-hover;
    }
    & ~ ul.tags {
        margin-top: 10px;
        margin-bottom: 10px;
    }
}

layouts/brands/brandGrid.scss:
// =============================================================================
// BRAND GRID (CSS)
// =============================================================================


// Brand grid view
// -----------------------------------------------------------------------------
//
// 1. Re-set the font-size, as we're reducing it to 0 in the .brandGrid class.
//
// -----------------------------------------------------------------------------
img.brand-logo-heading{
    border: 1px solid #d0d0d0;
    margin-bottom: 20px;
    padding: 10px;
    background: #ffffff;
}
.brandGrid {

    @include u-listBullets("none");
    @include grid-row($behavior: "nest");
    font-size: 0; // 1
    margin-bottom: spacing("single");
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    ms-flex-pack: center;
    justify-content: center;
    -ms-justify-content: center;
    -webkit-justify-content: center;
    flex-wrap: wrap;

    .brand {
        @include grid-column(6, $float: none);
        display: inline-block;
        font-size: fontSize("base"); // 1
        vertical-align: top;
        float: left;
        @include breakpoint("small") {
            width: grid-calc(4, $total-columns);
        }

        @media (min-width: 992px) {
            width: 19.95%;
        }
        @media (max-width: 991px) {
            width: 25%;
        }
        @media (max-width: 767px) {
            width: 33.30%;
        }
        @media (max-width: 480px) {
            width: 49.95%;
        }
        figure {
            border: 1px solid #d0d0d0;
            
        }
        // scss-lint:disable SelectorDepth, NestingDepth
        .card-title {
            text-align: center;
            font-size: 13px;;
            letter-spacing: 0;
            font-weight: 400;
            font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
        }
        .card-image{
            width: 100%;
        }
        .card-body{
            padding-bottom: 0.75rem;
        }
        .card {
            background-color: #fff;
            -webkit-border-radius: 3px;
            -moz-border-radius: 3px;
            border-radius: 3px;
            overflow: hidden;
            -webkit-box-shadow: 0 1px 5px rgba(0,0,0,.06);
            -moz-box-shadow: 0 1px 5px rgba(0,0,0,.06);
            box-shadow: 0 1px 5px rgba(0,0,0,.06);
            border-bottom: 1px solid #dcdcdc;
        }
    }
}

layouts/account/account.scss:
// =============================================================================
// ACCOUNT
// =============================================================================


// Account layouts
// -----------------------------------------------------------------------------
.wrap-account{
    -moz-box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.08);
    -webkit-box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.08);
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.08);
    background-color: #ffffff;
    border-radius: 3px;
    overflow: hidden;
    & .account {
        padding: 30px;
        .wishlists-table{
            margin: 0 0 15px 0;
            .table-thead{
                background: transparent;
                th{
                    border-width: 0 0 1px;
                }
            }
        }
    }
    & .account-body {
        padding: 0 !important;
    }
    ul.navBar-section li.navBar-item {
        text-transform:none;
    }
}

.account {
    margin-bottom: spacing("single");    
    .account-body {
        padding: 30px 30px 50px;
        form{
            margin-bottom: 0;
        }
    }
    .form-actions .btn-lg {
        min-width: 210px;
    }
}

.account-head {
    margin-bottom: spacing("double");
    text-align: center;

    .alertBox {
        padding-left: spacing("double");
        padding-right: spacing("double");
        text-align: left;
    }
}

.account-body {
    //@include grid-row($behavior: "nest");
}

.account-content {
   /* @include grid-column(12);*/
}

.account-content--fixed {

    @include breakpoint("medium") {
        width: grid-calc(8, $total-columns);
    }
}

.account-content--fixedSmall {

    @include breakpoint("medium") {
        width: grid-calc(6, $total-columns);
    }
}

.account-sidebar {
    @include grid-column(12);

    @include breakpoint("medium") {
        width: grid-calc(4, $total-columns);

        .account-content--fixedSmall + & {
            float: right;
        }
    }
    .account-sidebar-block {
        .order-details-info {
            .account-reorder-form {
                .btn {
                    background: #ea3a02;
                    border-color: #ea3a02;
                    color: #fff;
                    &:hover {
                        background: #000;
                        border-color: #000;
                        color: #fff;
                    }
                }
            }
        }
   }
}
/* Style tab account*/ 
ul.navBar-section {
    position: relative;
    width: 100%;
    border: 0;
    &:after {
        position: absolute;
        bottom: 0;
        width: 100%;
        z-index: 1;
        height: 1px;
        content: '';
        background: #eaeaea;
        @media (max-width: 991px) {
            position: static;
        }
    }
    & li.navBar-item {
         position: relative;
        z-index: 1;
        display: table;
        float: left;
        height: 53px;
        margin-left: -1px;
        border: 1px solid #eaeaea;
        border-top: 0;
        border-bottom: 0;
        font-size: 13px;
        font-weight: 700;
        color: #000;
        background: #ffffff;
        line-height: 51px;
        display: table-cell;
        padding: 0 15px;
        vertical-align: middle;
        text-transform: uppercase;
        font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
        text-decoration: none !important;
        @media (max-width: 991px) {
            width: 100%;
            margin-left: 0;
            margin-bottom: -1px;
            border: 0;
        }
        @media (min-width: 991px){
            &:first-child {
                margin-left: 0;
                border-left: 1px solid #ffffff;
                &.is-active {
                    &:before {                        
                        content: '';
                        border-left: 1px solid #2469d9;
                    }
                }
            }
            &:last-child {
               
            }
        }
        & a {
            font-size: 13px;
            font-weight: 700;
            display: block;
            display: table-cell;
            padding: 0 15px;
            line-height: 51px;
            -webkit-transition: background .3s ease 0s;
            -moz-transition: background .3s ease 0s;
            -ms-transition: background .3s ease 0s;
            -o-transition: background .3s ease 0s;
            transition: background .3s ease 0s;
            vertical-align: middle;
            text-transform: capitalize;
            color: #000;
            font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
            text-decoration: none !important;
            @media (max-width: 480px) {
                font-size: 12px;
                padding: 0 15px;
                letter-spacing: 0;
            }
            &:focus {
                outline: none;
            }
        }
        &.is-active {
            border-top: 0;
            border-bottom: 0;
            background: #fff;
            position: relative;
            z-index: 2;
            text-transform: capitalize;
            @media (max-width: 1200px) {
                border: 0;
                background: #00b9e4;
                color: #ffffff;
            }
            &:before {
                position: absolute;
                top: 0;
                left: 0px;
                width: 100%;
                height: 3px;
                z-index: 3;
                content: '';
                background: #2469d9;
                border-left: 1px solid #2469d9;
                border-right: 1px solid #2469d9;
                @media (max-width:1200px) {
                    background: #00b9e4;
                    border-left-color: #00b9e4;
                    border-right-color: #00b9e4;
                }
            }
            @media (min-width: 991px){
                &:after {
                    position: absolute;
                    bottom: -10px;
                    left: 0px;
                    width: 100%;
                    height: 10px;
                    z-index: 3;
                    content: '';
                    background: #ffffff;
                }
            }
        }
        &.is-active a {
            border-bottom-color: #fff;
            background: #fff;
            color: #000;
            @media (max-width: 1200px) {
                background: transparent;
                color: #fff;
                border-bottom-color: #fff;
            }
        }
        &:hover{
            // border-top: 1px solid;
            // border-bottom: 0;
            // background: #fff;
            // position: relative;
            // z-index: 2;
            // @media (max-width: 991px) {
            //     border: 0;
            // }
            // a {
            //     border-bottom-color: #fff;
            //     background: #fff;
            //     color: #000;
            //     @media (max-width: 991px) {
            //         background: #fff;
            //         border-bottom-color: #000;
            //     }
            // }
        }
    }
}

.page-type-account_recentitems {
    & .module-wrapper {
        & .productBlockContainer {
            & .prod-item {
                @media (max-width: 991px) {
                    padding: 25px 15px;
                }
            }
        }
    }  
    .product-pagination.bottom {
        padding: 20px 15px;
    }  
}

components/citadel/nav/nav.scss:
// =============================================================================
// NAV (CSS)
// =============================================================================
// Citadel inline messages
// -----------------------------------------------------------------------------
//
// 1. Remove the margin-left we add to default lists.
//
// -----------------------------------------------------------------------------
.navList,
.navBar {
    margin-left: 0; // 1
}

.navList-action,
.navBar-action {
    text-decoration: none;
    font-size: fontSize("base");
}

.navList-action.is-active {
    font-weight: $navList-action-fontWeight--active;
}

// NavList action checkbox
// -----------------------------------------------------------------------------
//
// Purpose: Display a checkbox next to an actionable link, without actually using
// a checkbox element.
//
// 1. Position checkbox so it is vertically centered
//
// -----------------------------------------------------------------------------
.navList-action--checkbox {
    @include checkRadioBase();
    @include checkBox-base();
    &:before,
    &:after {
        top: 50%; // 1
    }
    &:before {
        margin-top: -$checkRadio-outerSize / 2;
    }
    &:after {
        margin-top: -$checkRadio-outerSize / 2 + remCalc(1);
    }
    &:focus {
        @include checkRadioBase--focus();
    }
    &.is-disabled {
        @include checkRadioBase--disabled();
    }
    &.is-checked,
    &.is-selected {
        @include checkRadioBase--checked();
    }
}

// NavList action close
// -----------------------------------------------------------------------------
//
.navList-action-close {
    background: $navList-action-close-background;
    border-radius: $navList-action-close-borderRadius;
    display: inline-block;
    float: right;
    line-height: 0;
    opacity: 0;
    padding: $navList-action-close-padding;
    transition: $navList-action-close-transition;
    visibility: hidden;
    .icon {
        @include square($navList-action-close-iconSize);
        fill: stencilColor("color-textLink");
    }
    .navList-action.is-selected:hover & {
        opacity: 1;
        visibility: visible;
    }
}

// Page Subnavigation
// -----------------------------------------------------------------------------
nav.navBar--sub {
    display: block;
}
nav.navBar--sub ~ main {
    & h3 {
        font-size: 14px;
        letter-spacing: 0;
        font-weight: 700;
        margin-bottom: 1.5rem;
        margin-top: 0;
        text-transform: uppercase;
    }
    & h2 {
        font-size: 14px;
        letter-spacing: 0;
        font-weight: 700;
        margin-bottom: 1.5rem;
        margin-top: 0;
        text-transform: uppercase;
    }
}

@media (max-width: 991px) {
    nav.navBar--sub ul.navBar-section.account-navigation {
        display: flex;
        flex-wrap: wrap;
        & li.navBar-item {
            width: 100%;
            margin-left: 0;
            margin-bottom: -1px;
            &:last-child {
                margin-bottom: 0;
            }
        }
    }
}

@media (max-width: 991px) {
    nav.navBar--sub.navBar--account ul.navBar-section {
        display: flex;
        flex-wrap: wrap;
        & li.navBar-item {
            width: 100%;
            margin-bottom: -1px;
            &:nth-child(4) {
                margin-left: 0;
            }
            &:nth-child(4),
            &:nth-child(5),
            &:nth-child(6) {
                margin-bottom: 0;
            }
            @media (max-width: 767px) {
                width: 100%;
                margin-left: 0;
                &:nth-child(4),
                &:nth-child(5) {
                    margin-bottom: -1px;
                }
            }
        }
    }
}

