layouts/products/productList.scss:
// =============================================================================
// PRODUCT LIST (CSS)
// =============================================================================
// Product list
// -----------------------------------------------------------------------------
.productList {
   @include u-listBullets("none");
   .product {
      // scss-lint:disable NestingDepth
      + .product {
         margin-top: spacing("base") * 4;
      }
   }
}

// Product list item
// -----------------------------------------------------------------------------
.listItem {
   @include breakpoint("small") {
      @include grid-row($behavior: "nest");
      display: table;
   }
}

.listItem-figure,
.listItem-body {
   @include breakpoint("small") {
      display: table-cell;
      vertical-align: top;
   }
   @include breakpoint("large") {
      vertical-align: middle;
   }
}

// List figure
// -----------------------------------------------------------------------------
.listItem-figure {
   margin: 0 0 spacing("single");
   @include breakpoint("small") {
      margin-bottom: 0;
      @include breakpoint("small") {
         padding-left: spacing("half");
         padding-right: spacing("half");
         width: grid-calc(3, $total-columns);
      }
   }
}

.listItem-image {
   width: 100%;
}

.listItem-body {
   @include breakpoint("small") {
      padding-left: spacing("base") + spacing("sixth");
      padding-right: spacing("half");
      width: grid-calc(9, $total-columns);
   }
}

// List content
// -----------------------------------------------------------------------------
.listItem-content {
   @include breakpoint("large") {
      display: table;
   }
}

.listItem-details,
.listItem-actions {
   @include breakpoint("large") {
      display: table-cell;
      vertical-align: top;
   }
}

// List details
// -----------------------------------------------------------------------------
.listItem-details {
   @include breakpoint("large") {
      padding-right: spacing("double") * 2;
      >:last-child {
         margin-bottom: 0;
      }
   }
}

.listItem-rating {
   margin-bottom: spacing("quarter");
}

.listItem-brand {
   color: color("greys", "light");
   font-size: fontSize("base");
   margin: 0 0 spacing("eighth");
   @include breakpoint("medium") {
      font-size: fontSize("smaller");
      margin-bottom: 0;
   }
}

.listItem-title {
   font-size: fontSize("smaller");
   margin: 0 0 spacing("eighth");
   @include breakpoint("medium") {
      font-size: fontSize("small");
   }
   > a {
      text-decoration: none;
   }
}

.listItem-price {
   margin-bottom: spacing("base");
   @include breakpoint("medium") {
      font-size: fontSize("small");
   }
}

// List actions
// -----------------------------------------------------------------------------
//
// 1. Magic number width to align closer to the intended design.
//
// -----------------------------------------------------------------------------
.listItem-actions {
   @include breakpoint("large") {
      vertical-align: middle;
      width: 22%; // 1
      .button--compare {
         @include buttonSize("small");
      }
   }
   .button {
      margin-bottom: 0;
      @include breakpoint("large") {
         width: 100%;
         // scss-lint:disable NestingDepth
         + .button {
            margin: spacing("half") 0 0;
         }
      }
   }
}

// Product list when sidebar is present
// -----------------------------------------------------------------------------
.page-sidebar + .page-content {
   .productList {
      // scss-lint:disable SelectorDepth, NestingDepth
      .listItem-details {
         @include breakpoint("large") {
            padding-right: spacing("double");
         }
      }
      .listItem-actions {
         @include breakpoint("large") {
            width: 30%;
         }
      }
   }
}

.module-wrapper.productList .productBlockContainer {
   margin-left: 0;
   margin-right: 0;
}

.module-wrapper.productList .productBlockContainer .prod-item {
   width: 100% !important;
   padding: 0 !important;
   & .card-figure {
      display: flex;
      flex-wrap: nowrap;
      width: 100%;
      & .prod-image {
         width: 210px;
         margin-bottom: 0;
         @media (max-width: 480px) {
            width: 40%;
            min-width: 40%;
            & .btnQV {
               position: static;
               display: inline-block;
            }
         }
         a.btnWL {
             display: none;
             opacity: 1;
          }
      }
      & .prod-desc {
         width: calc(100% - 210px);
         padding-left: 30px;
         padding-bottom: 0px;
         text-align: left;
         & h4.prod-name {
            margin-bottom: 10px;
         }
         & .prod-summary {
            display: block;
            margin-bottom: 20px;
            & p {
               color: #2d2d2d;
               margin-bottom: 10px;
            }
         }
         & .star-rating {
            display: block !important;
            margin-bottom: 15px;
            opacity: 1;
            visibility: visible;
            
         }
         a.btnWL {
            span{
               font-weight: 400;
                color: #303030;
                font-size: 12px;
                letter-spacing: 0;
                border-bottom: 1px solid #989898;
                line-height: 1.3;
                display: inline-block;
            }
            i{
               padding-right: 10px;
               font-size: 15px;
               color: #505050;
            }
             &:hover{
               span,i{
                  color: $anchor-font-color-hover;
               }
             }
         }
         & .rating-compare{
            opacity: 1;
            visibility: visible;
         }
         .btn-compare {
            display: block;
            margin-top: 20px;
         }
         & .btnAbs{
              @media (max-width: 1199px){
                display: block!important;                
              }
              display: inline-block!important;
             margin-right: 20px;
             width: auto;
             min-width: 170px;
             margin-top: 12px;
         }
         & .btnATC {
            display: -moz-inline-flex !important;
            display: -webkit-inline-flex !important;
            display: inline-flex !important;
            margin-right: 25px;
            width: auto;
            min-width: 170px;
            border: 0;
            color: stencilColor('list-add-to-cart-color');
            background-color: stencilColor('list-add-to-cart-bgColor');
            i{
                padding-right: 10px;
                margin-top: 2px;
                font-size: 12px;
            }
            i.icon-cart{
                margin-right: 8px;
                width: 22px;
                height: 20px;
                position: relative;
                svg {
                    height: 25px;
                    width: 25px;
                    fill: #fff;
                    margin-right: 10px;
                }
              
            }
            &:hover {
               color: stencilColor('list-add-to-cart-colorHover');
               background-color: stencilColor('list-add-to-cart-bgColorHover');
            }
            @media (max-width: 1199px) {
               width: 100%;
               min-width: 100%;
               margin-bottom: 25px;
               margin-right: 0;
            }
            @media (max-width: 480px) {
               letter-spacing: 0;
               font-size: 11px;
               padding: 0;
               line-height: 35px;
            }
         }
         @media (max-width: 480px) {
            width: 60%;
            padding-left: 20px;
         }
      }
   }
   .card {
      padding: 30px;
      padding-bottom: 32px;
   }
   & + .prod-item > .card {
      padding-top: 30px;
   }
}

components/stencil/search/search.scss:
// =============================================================================
// SEARCH
// =============================================================================
#quickSearch{
    position: relative;
    width: 100%;
    padding: 0 20px;
    padding-bottom: 20px;    
    padding-top: 15px;    
    @media (max-width: 991px){
        padding: 16px 20px 20px 20px;
    }
     @media (max-width: 991px){
        padding: 16px 10px 20px 10px;
    }
}
#quickSearch > form {
    margin: 0;
    & .form-field {
        margin: 0;
        position: relative;
        width: 100%;
        border-radius: 3px;
        -moz-box-shadow: 0 1px 6px rgba(9, 63, 152, 0.40);
        -webkit-box-shadow: 0 1px 6px rgba(9, 63, 152, 0.40);
        box-shadow: 0 1px 6px rgba(9, 63, 152, 0.40);
        & input#search_query {
            font-size: 11px;
            height: 40px;
            padding: 7px 45px 6px 15px;
            margin: 0;
            border: 0;
            width: 100%;
            color: stencilColor('searchBox-textField-color');
            border-color: stencilColor('searchBox-textField-border-Color'); 
            -webkit-border-radius: 0;
            -moz-border-radius: 0;
            -ms-border-radius: 0;
            -o-border-radius: 0;
            border-radius: 0;
            -webkit-box-sizing: border-box;
            -moz-box-sizing: border-box;
            box-sizing: border-box;
            font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
            &::-webkit-input-placeholder {
                /* Chrome/Opera/Safari */
                color: stencilColor('searchBox-textField-placeholder');
                font-size: 11px;
                font-style: italic;
                letter-spacing: 0.095em; 
                opacity: 0.5;
                filter: alpha(opacity=50);
            }
            &::-moz-placeholder {
                /* Firefox 19+ */
                color: stencilColor('searchBox-textField-placeholder');
                font-size: 11px;
                font-style: italic;
                letter-spacing: 0.095em;
                opacity: 0.5;
                filter: alpha(opacity=50);
            }
            &:-ms-input-placeholder {
                /* IE 10+ */
                color: stencilColor('searchBox-textField-placeholder');
                font-size: 11px;
                font-style: italic;
                letter-spacing: 0.095em;
                opacity: 0.5;
                filter: alpha(opacity=50);

            }
            &:-moz-placeholder {
                /* Firefox 18- */
                color: stencilColor('searchBox-textField-placeholder');
                font-size: 12px;
                font-style: italic;
                letter-spacing: 0.095em;
                opacity: 0.5;
                filter: alpha(opacity=50);

            }
        }
        & button {
            cursor: pointer;
            border: 0 !important;
            vertical-align: top;
            margin: 0;
            width: 127px;
            height: 40px;
            padding: 0;
            position: absolute;
            top: 0;
            right: 0;
            -webkit-border-top-right-radius: 0px;
            -webkit-border-bottom-right-radius: 0px;
            -moz-border-radius-topright: 0px;
            -moz-border-radius-bottomright: 0px;
            border-top-right-radius: 0px;
            border-bottom-right-radius: 0px;
            -moz-transition: background-color 150ms ease;
            -webkit-transition: background-color 150ms ease;
            -o-transition: background-color 150ms ease;
            transition: background-color 150ms ease;
            color: stencilColor('searchBox-button-color');
            background: stencilColor('searchBox-button-backgroundColor');
            -webkit-transition: all .3s ease 0s;
            transition: all .3s ease 0s;
            text-transform: uppercase;
            letter-spacing: 0.095em;
            font-weight: 500;
            @media (max-width: 480px){
                width: 40px;
                height: 40px;
                bottom: 0;
                top: auto;
            }
            &:hover {
                -webkit-transition: all .3s ease 0s;
                transition: all .3s ease 0s;
                color: stencilColor('searchBox-button-color-hover');
                background: stencilColor('searchBox-button-backgroundColor-hover');
            }
            @media (max-width: 767px) {
                background: transparent;
                width: 40px;
                margin-left: -40px;
                font-size: 0;
            }
            & svg {
                width: 18px;
                height: 19px;
                fill: stencilColor('searchBox-button-backgroundColor');
                margin-top: 7px;
                @media (min-width: 768px) {
                    display: none;
                }
            }


        }
        #search_category{
            width: 55px;            
            background: stencilColor('searchBox-textSelect-backgroundColor');
            color: stencilColor('searchBox-textSelect-color');
            font-weight:700;
            border-radius: 3px 0 0 3px;
            -moz-border-radius: 3px 0 0 3px;
            overflow: hidden;
            min-height:15px;
            float:left;
            font-size:13px;
            font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
            height: 40px;
            line-height:20px;
            position: relative;
            select{
                border:none;
                height: 40px;
                appearance: none!important;
                -webkit-appearance: none!important;
                position: relative;
                z-index: 2;
                background: transparent;
                padding: 8px 15px 8px 12px;
            }
            i{
                font-size: 10px;
                position: absolute;
                top: 14px;
                right: 10px;
            }
            option{                 
                font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
            }

        }
        // @media(max-width: 480px){
        //     box-shadow: none;
        //     #search_category{
        //         width: 100% !important;
        //         margin-bottom: 10px;
        //         -webkit-border-radius: 4px;
        //         -moz-border-radius: 4px;
        //         -ms-border-radius: 4px;
        //         -o-border-radius: 4px;
        //         border-radius: 4px;
        //     }
        // }
    }
}

#quickSearch .quickSearchResultsWrap {
    position: absolute;
    width: calc(100% - 135px);
    min-width: 290px;
    left: 30px;
    z-index: 98;
    top: auto;
    opacity: 0;
    visibility: hidden;
    @media (max-width: 991px) {
        width: calc(100% - 20px);
        left: 10px;
    }
    @media (max-width: 480px) {
        width: 100% ;
        padding-left: 0;
        padding-right: 24px;
        right: auto;
        left: auto;
    }

    &.hasResults {
        opacity: 1;
        visibility: visible;
    }
    &.hasNoResults {
        & .quickSearchResults {
            padding: 15px;
        }
        & .alert {
            margin: 0;
            & .quickSearchMessage {
                margin: 0;
                font-size: fontSize("base");
            }
        }
    }
}

#quickSearch .quickSearchResults {
    background: #fff;
    -webkit-box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
    -moz-box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
    box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
    border: 1px solid #d0d0d0;
    background: #fff;
    padding: 0;
    overflow: hidden;
    max-height: 405px;
    border-radius: 3px;
    @media (max-width: 991px) {
        margin-top: 0;
    }
    & .productItems {
        margin: 0;
    }
    & .productItems > li {
        padding: 0;
        width: 100%;
        list-style: none;
        float: none;
        clear: both;
        padding: 15px;
        overflow: hidden;
        & + li {
            border-top: 1px solid #eaeaea;
        }
        &:hover {
            background: #f5f5f5;
        }
        & article {
            margin: 0;
            padding: 0;
            border: 0;
            & figure {
                float: left;
                width: 20%;
                text-align: center;
                border-radius: 3px;
                margin: 0;
                background-color: #fff;
                & a img {
                    max-height: 70px;
                }
            }
            & figcaption {
                float: left;
                margin: 0;
                text-align: left;
                width: 80%;
                padding-left: 15px;
                & h4 {
                    margin: 0 0 8px;
                    font-size: 12px;
                    font-weight: 500;
                    line-height: 22px;
                    letter-spacing: 0;
                    font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
                    & a{
                        color: stencilColor('card-title-color');
                    }
                    &:hover a {
                        color: stencilColor('card-title-color-hover');
                        text-decoration: none;
                    }
                }
                & .item-price {
                    margin-bottom: 5px;
                    color: #000;
                    font-weight: 700;
                    & .price--rrp {
                        color: #969696;
                        margin-right: 5px;
                        & ~ .price--withoutTax {
                            color: #e95144;
                        }
                    }
                }
            }
        }
    }
}

@media (max-width: 991px) {
    .mobile-search-toggle {
        width: 62px;
        height: 100%;
        position: absolute;
        left: 0;
        top: 0;
        padding: 0;
        & svg {
            width: 20px;
            height: 100%;
            padding: 3px;
            fill: stencilColor('headerTop-textColor');
        }
        & svg:first-child {
            display: none;
        }
        &:hover svg {
            fill: stencilColor('headerTop-textColorHover');
        }
    }
    #quickSearch.searchSidebar {
        position: relative;
        top: 0;
        left: 0;
        width: 100%;
        min-width: auto;
        z-index: 99;
        padding: 11px 12px 12px;
        background-color: #2e305a;
        display: block;
        margin-top: 1px;
        & > form .form-field {
            max-width: 100%;
            border: 0;
            border-radius: 3px;
            & input#search_query {
                padding-right: 50px;
                width: 100% !important;
                -webkit-border-radius: 4px;
                -moz-border-radius: 4px;
                -ms-border-radius: 4px;
                -o-border-radius: 4px;
                border-radius: 4px;

            }
            & button {
                width: 40px;
                height: 40px;
                bottom: 0;
                top: auto;
            }
        }
    }
    body.st-off-canvas {
        overflow: hidden;
        & .mobile-search-toggle {
            & svg:last-child {
                display: none;
            }
            & svg:first-child {
                display: inline-block;
                width: 28px;
            }
            & ~ .navUser > ul > li#quickSearch {
                display: block;
            }
        }
       
    }
}


.search-suggestion {
    margin-bottom: spacing("single") + spacing("third");
    >:last-child {
        margin-bottom: 0;
    }
}

.suggestion-title {
    margin-top: 0;
    text-transform: inherit;
}

.advancedSearch-form {
    margin-bottom: spacing("double");
    .form-row {
        margin-bottom: -(spacing("single"));
    }
}

.advancedSearch-separator {
    display: none;
}

.advancedSearch-title {
    margin: 0 0 spacing("single");
    text-transform: inherit;
}

.search-price-range {
    label {
        display: inline;
        // scss-lint:disable SelectorDepth, NestingDepth
        input {
            display: block;
            padding-left: spacing("single");
            @include breakpoint("small") {
                display: inline;
                width: 6rem;
            }
        }
        // scss-lint:disable SelectorDepth, NestingDepth
        span {
            @include breakpoint("small") {
                display: inline;
                padding-left: spacing("single");
                padding-right: spacing("single");
            }
        }
    }
}

.category-suggestion-list {
    @include grid-row($behavior: "nest");
    list-style-type: none;
}

.category-suggestion {
    @include grid-column(4);
    font-size: fontSize("smaller");
}

.search-refine {
    margin-left: spacing("single");
}

.quickSearchMessage {
    font-size: fontSize("largest");
    margin: spacing("single") 0 0;
    text-align: center;
}



.page-type-search {
    & .product-pagination.top {
        justify-content: space-between;
        -webkit-box-pack: justify;
        -ms-flex-pack: justify;
        flex-wrap: wrap;
        ddisplay: -ms-flexbox;
        display: -webkit-flex;
        display: flex;
        @media (max-width: 767px) {
            display: block;
            width: 100%;
            float: left;
        }
        & .view-mode-btn {
             @media (max-width: 1199px) {
                margin-bottom: 12px;
             }
             @media (max-width: 550px) {
                margin-right: 0;
             }
        }
        & .actionBar[data-sort-by] {
            width: auto;
            clear: both;
            float: none;
            @media (max-width: 550px) {
                right: 0;
            }
            & fieldset {
                @media (max-width: 550px) {
                    padding-right: 0;
                    
                }
            }
        }

    }
    .panel-body{
        background: #fff;
        padding: 0;
    }
    .search-suggestion {
        border: 1px solid #d7d7d7;
        border-radius: 3px;
        overflow: hidden;
    }
    .suggestion-title{
         font-family: $fontFamily-headings;
        background: #f8f8f8;
        padding-top: 0;
        border-bottom: 1px solid #d7d7d7;
        padding: 20px 20px 17px;
        margin-top: 0;
        margin-bottom: 0;
        font-size: 13px;
        letter-spacing: 0;
        text-align: left;
        font-weight: bold;
        color: #2f2f2f;
        line-height: 1.1;
        text-transform: uppercase;
    }
    .category-suggestion-list {
        padding: 20px;
    }
    .category-suggestion{
        font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
        font-size: 13px;
        font-weight: 500;
        line-height: 22px;
        letter-spacing: 0;
        color: #787878;
         & a{

                color: stencilColor('card-title-color');
                &:hover{
                    color: stencilColor('card-title-color-hover');
                    text-decoration: none;
                }
            }
            
    }
}

components/stencil/account/account.scss:
// =============================================================================
// ACCOUNT (CSS)
// =============================================================================


// Account navigation
// -----------------------------------------------------------------------------

.navBar--account,
.navBar--sub {
    display: none;

    @include breakpoint("medium") {
        display: block;
    }
    ul {
        text-align: left;
        li:first-child {
            margin-left: 0;
        }
    }
    ul li a {
        &:hover {
            text-decoration: none;
        }
    }


}


// Account headings
// -----------------------------------------------------------------------------

.account-heading {
    border-bottom: container("border");
    margin: 0;
    padding-bottom: spacing("base");
    font-size: 14px;
    font-weight: 700;
    text-transform: uppercase;
}


// Account list
// -----------------------------------------------------------------------------

.account-list {
    list-style: none;
    margin-left: 0;
}

.account-listItem {
    border-bottom: container("border");
    padding: spacing("single") 0;
    position: relative;

    img {
        width: 100%;
    }

    .account-product-image {
        width: auto;
    }
}

.account-listShipping {
    border-bottom: container("border");
    font-weight: fontWeight("bold");
    padding: (spacing("single") + spacing("third")) 0;

    .account-listShipping-title {
        margin: 0;
        text-transform: inherit;
    }
}

// Account product
// -----------------------------------------------------------------------------

.account-product {
    @include media;
}

.account-product-figure {
    align-items: center;
    display: flex;
    flex-direction: column;
    margin: 0 spacing("single") + spacing("third") 0 0;
    position: relative;
    width: 70px;
}

.account-product-download {
    border-radius: 50%;
    height: remCalc(33px);
    padding: 0;
    position: absolute;
    right: -(spacing("half"));
    top: -(spacing("half"));
    width: remCalc(33px);
    z-index: 1;

    .icon {
        height: spacing("single");
        margin-top: spacing("quarter");
        width: spacing("single");
    }

    svg {
        fill: color("whites", "bright");
    }
}

.account-product-body {
    overflow: hidden;
    padding-bottom: spacing("double") + spacing("third");
    position: relative;

    @include breakpoint("small") {
        padding-bottom: 0;
    }

    .definitionList {
        font-size: fontSize("smaller");
        margin-bottom: 0;
        clear: both;
    }
}

.account-product-title {
    margin: 0 0 spacing("eighth");
    text-transform: inherit;
    letter-spacing: 0;
    font-size: 14px;
    font-family: 'Roboto';
    font-weight: 700;
    line-height: 20px;
    > a {
        text-decoration: none;
    }

    + .definitionList {
        margin-top: spacing("half");
    }
}

.account-product-subtitle {
    color: color("greys", "light");
    font-family: fontFamily("sans");
    font-size: fontSize("smaller");
    margin: -(spacing("eighth")) 0 spacing("base");
    text-transform: inherit;
}

.account-product-price {
    float: left;
    font-size: fontSize("smaller");
    padding-top: 10px;
    width: 100%;
    margin-bottom: 10px;
}

.account-product-description + .definitionList {
    margin: -(spacing("base") + spacing("quarter")) 0 spacing("single");
}

.account-product-refundQty {
    color: color("greys", "light");
    margin-bottom: 0;
}

.account-product-details {
    @include grid-row($behavior: "nest");
}

.account-product-detail {
    @include grid-column(12);

    @include breakpoint("small") {
        width: grid-calc(4, $total-columns);
    }

    @include breakpoint("large") {
        width: grid-calc(3, $total-columns);
    }

    + .account-product-detail {
        margin-top: spacing("base");

        @include breakpoint("small") {
            margin-top: 0;
        }
    }

    + .account-product-detail--full {

        @include breakpoint("small") {
            margin-top: spacing("single");
        }
    }
}

.account-product-detail--large {

    @include breakpoint("large") {
        width: grid-calc(6, $total-columns);
    }
}

.account-product-detail--full {
    width: 100%;
}

.account-product-detail-heading {
    color: stencilColor("color-textSecondary");
    font-family: fontFamily("sans");
    font-size: fontSize("tiny");
    margin: 0 0 spacing("quarter");
}


// Vertically aligned product view
// -----------------------------------------------------------------------------

.account-product--alignMiddle {
    display: table;
    width: 100%;

    .account-product-checkItem {
        display: table-cell;
        position: relative;
        vertical-align: middle;
        width: 2rem;
        padding-left: 30px;
        @include breakpoint("large") {
            left: -2.5rem;
        }

        // scss-lint:disable SelectorDepth, NestingDepth
        .form-label {
            height: spacing("single");
            margin: 0;
            padding: 0;
            width: spacing("single");
        }
    }

    .account-product-figure,
    .account-product-body {
        display: table-cell;
    }

    .account-product-figure {
        float: none;
        margin-right: 0;

        @include breakpoint("large") {
            left: -2rem;
        }
    }

    .account-product-body {
        padding-left: 2rem;
        vertical-align: middle;

        @include breakpoint("large") {
            padding-left: 0;
        }
    }
}


// Account product totals
// -----------------------------------------------------------------------------

.account-orderTotal {
    float: right;
    font-size: fontSize("smaller");
    margin-top: -(spacing("single"));
    width: grid-calc(8, $total-columns);
}

.account-orderTotal-key,
.account-orderTotal-value {
    margin: 0;
    padding: spacing("single") 0;
}

.account-orderTotal-key {
    color: stencilColor("color-textSecondary");
    float: left;
    font-weight: fontWeight("normal");

    &:last-of-type {
        position: relative;
        top: spacing("fifth");
    }
}

.account-orderTotal-value {
    border-bottom: container("border");
    text-align: right;

    &:last-child {
        border-bottom: 0;
        font-size: fontSize("small");
    }
}


// Account status
// -----------------------------------------------------------------------------

.account-orderStatus {
    bottom: 0;
    left: 0;
    position: absolute;

    @include breakpoint("small") {
        float: right;
        position: inherit;
        text-align: right;
    }
}

.account-orderStatus-label {
    background-color: stencilColor("label-backgroundColor");
    color: stencilColor("label-color");
    display: inline-block;
    font-size: fontSize("tiny");
    line-height: lineHeight("smallest");
    margin: 0 0 spacing("quarter");
    padding: spacing("eighth") spacing("third");
}

.account-orderStatus-action {
    color: stencilColor("color-textSecondary");
    display: inline-block;
    font-size: fontSize("tiny");
    font-style: italic;
    letter-spacing: letterSpacing("large");
    margin-left: spacing("half");
    text-align: right;
    text-decoration: underline;
    vertical-align: top;

    @include breakpoint("small") {
        display: block;
        margin-left: 0;
    }

    &:hover {
        color: stencilColor("color-textSecondary--hover");
    }
}


// Account pagination & panel
// -----------------------------------------------------------------------------

.account {

    .pagination {
        color: color("greys", "light");
        margin-top: spacing("double");
        text-align: center;
    }
    .productBlockContainer{
        margin-left: 0 !important;
        margin-right: 0 !important;
    }
}


// Account inbox
// -----------------------------------------------------------------------------

.account-message {

    .is-read {
        color: color("greys", "light");
    }
}


// Sidebar blocks
// -----------------------------------------------------------------------------

.account-sidebar-block {
    font-size: fontSize("base");

    + .account-sidebar-block {
        margin-top: spacing("double");
    }

    .account-heading {
        margin-bottom: spacing("single");
    }
}

.account-order-address {
    @include u-listBullets("none");
}

.account-reorder-form {
    display: inline-block;
}

.order-payments-description {
    color: stencilColor("color-primary");
    font-weight: fontWeight("normal");
    margin-top: spacing("single");
}


// Product downloads
// -----------------------------------------------------------------------------

.account-downloads-summary {
    border-bottom: container("border");
    font-size: fontSize("smaller");
    padding-bottom: spacing("single");
}

.account-downloadsList {
    @include u-listBullets("none");
}

.account-downloadsItem {
    margin-bottom: spacing("single");

    > :first-child {
        margin-top: 0;
    }

    > :last-child {
        margin-bottom: 0;
    }
}

.account-downloadsItem-title {
    color: stencilColor("color-textSecondary");
    font-size: fontSize("smaller");
    margin-bottom: spacing("eighth");
    text-transform: inherit;
}

.account-downloadsItem-description {
    margin-bottom: 0;
}

.account-downloadsItem-availability {
    color: stencilColor("color-textSecondary");
}
.login{
    .form-field{
        @media (min-width: 1200px){
            width: 470px;
        }        
    }
    .login-row {
        background: #ffffff;
        border-radius: 5px;
        -moz-box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1);
        -webkit-box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1);
        box-shadow: 0 1px 4px rgba(0, 0, 0, 0.1);
        @media (max-width: 991px){
            padding: 20px;
        }
    }
}

