#mobile-categories {
    .cateArea {
        max-width: 100% !important;
        padding-bottom: 25px;
        clear: both;
    }

    .btnAbs a{
        padding: 0;
        line-height: 33px;
    }
    .descArea {
        max-width: 100% !important;
        padding: 20px;
        float: none;
    }
    .rightCate {
        position: static;
        margin-top: 20px;
        .right-cates {
            position: static !important;
            width: 100%;
            margin: 0;
            padding: 0 20px 0;
        }
    }
    .bottomMegamenu{
        padding: 0 20px 20px;
        .col-img-full{
            text-align: center;
            a{
                margin-top: 20px;
                display: inline-block;
            }
            a + a {
                margin-bottom: 20px;
                margin-left: 0;
            }
        }
    }
    .bottomCate {
        position: relative;
        max-width: 100% !important;
        float: left;
        .col-left {
            width: 100%;
            padding: 20px 0 20px 0;
        }
        .col-right {
            width: 100%;
            padding-bottom: 0;
        }
    }

    // 
    #mobileAccountSidebar{
        a{
            text-transform: capitalize;
            border-bottom: 0;
        }
    }
    li.subAccount{
        a,
        .logged-in{
            width: 100%;
            display: block;
            padding: 0px 20px 10px;

        }
    }
    .mobile-currency{
        .toggle-expand{
            opacity: 0;
        }
        a.navUser-action{
            padding: 13px 20px 12px;
            border-top: 1px solid #e5e5e5;
            border-bottom: 1px solid #e5e5e5;   
            display: block;         
            position: relative;
            i.fa-angle-down:before{
                   position: absolute;
                    top: 45%;
                    right: 16px;
                    content: '';
                    border: 5px solid transparent;
                    border-top: 5px solid #000;
            }
            &.is-open{
                i.fa-angle-down:before{
                    position: absolute;
                    top: 35%;
                    right: 16px;
                    content: '';
                    border: 5px solid transparent;
                    border-bottom: 5px solid #000;
                }
            }
        }
        &:hover{                
            background: #f5f5f5;
            span{
                color: #094996;
            }
            a.navUser-action{
                &.is-open{
                        i.fa-angle-down:before{
                        border-bottom-color: #094996;
                        border-top: 0;
                        top: 45%;
                    }
                }
                i.fa-angle-down:before{
                    border-top-color: #094996;
                }                
            }
        }
        span{
            font-size: 14px;
            font-weight: 700;
            letter-spacing: 0;
            text-transform: uppercase;
            color: #000;
            padding-left: 10px;
            outline: none;
        }
        .dropdown-menu.is-open {
            position: static !important;
            max-width: 100% !important;
            width: 100% !important;
            border: 0;
            box-shadow: none;
        }
        .dropdown-menu-item{
            padding: 0;
            a{
                margin: 0;
                padding: 13px 20px 12px;
                strong,
                span{
                    padding-left: 10px;
                    color: #000;
                    &:hover{
                        color:  $anchor-font-color-hover;
                    }
                }
                span{
                    font-weight: 400;
                }
                img {
                    margin-top: -4px;
                }
            }
        }
    }

}

layouts/sidebar/block.scss:
.page-type-category .page,
.page-type-brand .page,
.page-type-search .page {
    display: -webkit-box;
    /* OLD - iOS 6-, Safari 3.1-6 */
    display: -moz-box;
    /* OLD - Firefox 19- (buggy but mostly works) */
    display: -ms-flexbox;
    /* TWEENER - IE 10 */
    display: -webkit-flex;
    /* NEW - Chrome */
    display: flex;
    /* NEW, Spec - Opera 12.1, Firefox 20+ */
    flex-wrap: nowrap;
    margin: 0;
    @media (max-width: 991px) {
        flex-wrap: wrap;
    }
}

// =============================================================================
// SIDEBAR BLOCKS (CSS)
// =============================================================================
aside.page-sidebar.col-sm-3 {
    width: 20%;
    min-width: 240px;
    padding: 0;
    @media (min-width: 992px) and (max-width: 1199px) {
        width: 20%;
    }
    @media (min-width: 992px) {
        & > nav {
            display: block !important;
        }
    }
    @media (max-width: 991px) {
        width: 100%;
        & > nav {
            display: none;
        }
    }
}
@media (min-width: 992px) {
    .page-type-category {
        & aside.page-sidebar {
            & > nav {
                display: block !important;
            }
        }
    }
}
@media (max-width: 991px) {
    .page-type-category {
        & aside.page-sidebar {
            & > nav {
                display: none;
            }
        }
    }
}


@media (max-width: 991px) {
    #sidebar-toggle {
        display: block !important;
        clear: both;
        -webkit-box-shadow: 0 2px 5px rgba(0,0,0,.08);
        -moz-box-shadow: 0 2px 5px rgba(0,0,0,.08);
        box-shadow: 0 2px 5px rgba(0,0,0,.08);
        border-bottom: 1px solid #dcdcdc;
        background-color: #ffffff;
        border-radius: 3px;
        overflow: hidden;
        margin: 0 auto;
        max-width: 100rem;
        width: 100%;
        margin-bottom: 28px;
        padding: 14px 20px;
        .title-sidebar-toggle {
            padding-left: 35px;
            padding-right: 5px;
            display: inline-block;
            text-transform: uppercase;
            font-size: 17px;
            font-weight: 500;
            background: url("/assets/img/filter-icon.png") no-repeat;
        }
        
        .btn-sidebar-toggle {
            display: inline-block;
            padding: 0;
            font-size: 19px;
            font-weight: bold;
            color: stencilColor('color-textLinkSecondary');
        }
        
        & a {
            display: -webkit-box;
            /* OLD - iOS 6-, Safari 3.1-6 */
            display: -moz-box;
            /* OLD - Firefox 19- (buggy but mostly works) */
            display: -ms-flexbox;
            /* TWEENER - IE 10 */
            display: -webkit-flex;
            /* NEW - Chrome */
            display: flex;
            /* NEW, Spec - Opera 12.1, Firefox 20+ */
            flex-wrap: nowrap;
            text-align: center;
            text-transform: capitalize;
            display: block;
            color: stencilColor('color-textBase');
            &:hover {
                color: stencilColor('color-textBase--hover');
            }
            & i {
                font-size: 14px;
                line-height: 20px;
            }
        }
    }
}

aside.page-sidebar.col-sm-3 ~ main.page-content.col-sm-9 {
    width: calc(100% - 240px);
    padding: 0;
    padding-left: 30px;
    @media (min-width: 992px) and (max-width: 1199px) {
       width: calc(100% - 240px);
    }
    @media (max-width: 991px) {
        width: 100%;
        padding-left: 0;
    }
}

.sidebarBlock {
    margin-bottom: 20px;
    clear: both;
    border: 1px solid stencilColor('border-base-color');
    background-color: #ffffff;
    -o-border-radius: 5px;
    -ms-border-radius: 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;

    overflow: hidden;
    .pad-block {
        padding: 20px 20px 0;
    }  
    ul.navList > li.navList-item {
        padding: 5px 0;
        & > a.navList-action {
            padding: 0;
            display: inline;
            &:hover {
                border-bottom: 1px solid $anchor-font-color-hover;
            }
        }
    }
}
#sideBannerImage.sidebarBlock-img {
    border: 0;
    text-align: center;
    padding-top: 0;
    border-radius: 3px;
    overflow: hidden;
    margin-bottom: 20px;
}

ul.all-categories-list {
    margin: 0;
    list-style: none;
    & a {
        font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
        font-size: 13px;
        border-bottom: 1px solid transparent;
        padding: 0;
        letter-spacing: 0;
        display: inline-block;
    }
    & i.fa.fa-chevron-up {
        position: absolute;
        right: 9px;
        padding: 8px;
        width: 30px;
        text-align: center;
        top: 10px;
        cursor: pointer;
        font-size: 10px;
        color: #cacaca;
        &.is-clicked::before {
            content: "\f054";
            position: relative;
            top: -1px;
            color: #fb641b;
        }
    }
    & > li {
        padding: 15px 20px 12px;
        position: relative;
        border-top: 1px solid #eaeaea;
        &:first-child{
            border-top: 0;
        }
        & > a {
            font-weight: 700;
            color: #2f2f2f;
             border-bottom: 1px dotted transparent;
            line-height: 1.3;
            &:hover {                
                color: $anchor-font-color-hover;
                border-bottom: 1px dotted $anchor-font-color-hover;
                line-height: 1.3;
            }
        }

        &.current-cat  >a {
            color: $anchor-font-color-hover;
            line-height: 1.3;
        }
        &.current-cat {
            & i.fa.fa-chevron-up {
                 &.is-clicked::before {
                    content: "\f054";
                    position: relative;
                    top: -1px;
                    color: #fb641b;
                }
            }
        }
        & > ul {
            margin: 0;
            border-left: 0 !important;
            & > li {
                & > ul {
                    border: 0 !important;
                }
                &:last-of-type {
                    border-bottom-color: transparent;
                }
            }
        }
        & ul.dropdown-category-list {
            opacity: 0;
            visibility: hidden;
            height: 0;
            margin: 0;
            border-left: 1px dotted #eaeaea;
            &.cat-expanded {
                opacity: 1;
                visibility: visible;
                height: auto;
                padding-top: 8px;
            }

        }
        & #navPages-level-3 > li {
            & > a {
                color: #2f2f2f;
                font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
                line-height: 1.3;
                display: inline-block;
                font-weight: 400;
                &:hover {
                    color: $anchor-font-color-hover;
                    
                }
            }

            &.current-cat {        
                &::before {
                    position: absolute;
                    left: 20px;
                    top: 6px;
                    color: #cacaca;
                    filter: alpha(opacity=100);
                    content: "\f0da";
                    font: normal normal normal 13px/1 FontAwesome;
                    transition: all .3s ease 0s;

                }
                
                & a {
                    padding-left: 0;
                    margin-left: 15px;
                    line-height: 1;
                    color: $anchor-font-color-hover !important;
                    display: inline-block;
                }
            }
        }
    }
    & ul {
        list-style: none;
        & >li {
            position: relative;
            padding: 10px 0;
            border-top: 1px dotted #eaeaea;
            i{
                right: -11px!important;
                top: 5px!important;
                color: #cacaca;
                z-index: 1;
            }
            & a {
                display: inline-block;
                position: relative;
                color: $anchor-font-color;
                font-size: 12px;
                font-weight: 700;
                letter-spacing: 0;
                line-height: 1.1;
                &:hover {
                    text-decoration: none;
                    color: $anchor-font-color-hover;
                    
                }
            }
            &.current-cat  >a {
                text-decoration: none;
                color: $anchor-font-color;
                font-weight: 600;
            }
            & .selected{
                font-weight: 600;
