layouts/footer/footer.scss:
// =============================================================================
// FOOTER (CSS)
// =============================================================================
#subscribe_form, #embed-newsletter {
    position: relative;
    background: #fff;
    border: 1px solid #d3d3d3;
    padding: 8px 90px 8px 110px;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    -ms-border-radius: 4px;
    -o-border-radius: 4px;
    border-radius: 4px;
}

#subscribe_form #nl_first_name, #embed-newsletter .name {
    width: 90px;
    border-right: 1px solid #e9e9e9;
    position: absolute;
    left: 10px;
    top: 8px;
}
#SearchForm .Button, #subscribe_form .btn {
    background: #111011;
    background: -moz-linear-gradient(top, #111011 0, #161516 51%, #1e1d1e 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0, #111011), color-stop(51%, #161516), color-stop(100%, #1e1d1e));
    background: -webkit-linear-gradient(top, #111011 0, #161516 51%, #1e1d1e 100%);
    background: -o-linear-gradient(top, #111011 0, #161516 51%, #1e1d1e 100%);
    background: -ms-linear-gradient(top, #111011 0, #161516 51%, #1e1d1e 100%);
    background: linear-gradient(to bottom, #111011 0, #161516 51%, #1e1d1e 100%);
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='darken(@color-search-button,5%)', endColorstr='@color-search-button', GradientType=0);
}

#subscribe_form .Textbox, #embed-newsletter .Textbox {
    display: inline-block;
    border: none;
    background: none;
    height: 24px;
    padding: 5px;
    color: #bababb;
    text-transform: lowercase;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}
.newsletter-subscription label {
    display: none;
}



#footer-wrapper {
    background: stencilColor('footer-backgroundColor');
    .social-icons{
        padding-right: 0;
        text-align: center;
    }
}


/*Main Footer*/

#main-footer{
    background: stencilColor('main-footer-background');
    padding-top: 50px;
    .container {
        @media (max-width: 991px) {
            padding-bottom: 0;
        }
        @media (max-width: 380px) {
            padding-left: 5px;
            padding-right: 5px;
        }
    }
} 

#main-footer .col-1,
#main-footer .col-2,
#main-footer .col-3 {
    float: left;
    width: 20%;
    padding: 0 15px;
    @media (max-width: 991px) {
        width: 33.33%;
        margin-bottom: 20px;
    }
    @media (max-width: 480px) {
        width: 50%;
    }
    @media (max-width: 320px) {
        width: 100%;
    }
}

@media (max-width: 480px) {
    #main-footer .col-3 {
        width: 100%;
    }
}

#main-footer .col-4 {
    float: left;
    width: 40%;
    padding: 0 15px;
    @media (max-width: 991px) {
        width: 100%;
    }
}

#main-footer h5 {
    font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
    font-size: 12px;
    font-weight: 700;
    margin: 0;
    letter-spacing: 0;
    margin-bottom: 5px;
    width: 100%;
    text-transform: uppercase;
    color: stencilColor('footer-column-heading-color');
}
.wrapSocialBlock h5{
    font-weight: 400 !important;
}
.footer-columns {
    display: -webkit-box;
    /* OLD - iOS 6-, Safari 3.1-6 */
    display: -moz-box;
    /* OLD - Firefox 19- (buggy but mostly works) */
    display: -ms-flexbox;
    /* TWEENER - IE 10 */
    display: -webkit-flex;
    /* NEW - Chrome */
    display: flex;
    /* NEW, Spec - Opera 12.1, Firefox 20+ */
    flex-wrap: wrap;
    max-width: 830px;
    margin: 0 auto;
    @media (max-width: 991px) {
        max-width: 100%;
    }
    @media (max-width: 320px) {
        margin-left: 0px;
        margin-right: 0px;
    }
    & .column-item  {
        margin-bottom: 25px;
        @media (min-width: 768px) {
            width: 24.95%;
        }
        @media (max-width: 991px) {
            margin-bottom: 30px;
        }
        @media (max-width: 767px) {
            width: 49%;
