        }
        @media (max-width: 320px) {
            padding: 0 10px;
        }
        p.noteSubcribeForm{
            line-height: 25px;
            color: stencilColor('footer-column-text-color');  
            margin-bottom: 5px;
        }
        & ul {
            margin-left: 0;
            list-style: none;
            & >li {
                color: stencilColor('footer-column-text-color'); 
                line-height: 25px;
                a {
                    line-height: 25px;
                    color: stencilColor('footer-column-text-color'); 
                    text-transform: normal;
                    font-size: 12px;
                    &:hover {
                        border-bottom: 1px solid stencilColor('footer-column-text-color-hover');
                        color: stencilColor('footer-column-text-color-hover');
                        &.icon{
                            border: 0;
                        }
                    }                   
                }
            }
        }
    }
}

.bottom-columns {
    display: -webkit-box;
    /* OLD - iOS 6-, Safari 3.1-6 */
    display: -moz-box;
    /* OLD - Firefox 19- (buggy but mostly works) */
    display: -ms-flexbox;
    /* TWEENER - IE 10 */
    display: -webkit-flex;
    /* NEW - Chrome */
    display: flex;
    /* NEW, Spec - Opera 12.1, Firefox 20+ */
    flex-wrap: wrap;
    padding-top: 50px;
    & .column-item {
        width: 33.33%;
        text-align: center;
        margin-bottom: 25px;
        padding-bottom: 25px;
        @media (max-width: 767px) {
            width: 100%;
            border-bottom: 1px solid stencilColor('footer-separated-border-color');
            padding-bottom: 15px;
            &:last-child {
                border: 0;
            }
        }
        & ul {
            display: -webkit-box;
            /* OLD - iOS 6-, Safari 3.1-6 */
            display: -moz-box;
            /* OLD - Firefox 19- (buggy but mostly works) */
            display: -ms-flexbox;
            /* TWEENER - IE 10 */
            display: -webkit-flex;
            /* NEW - Chrome */
            display: flex;
            /* NEW, Spec - Opera 12.1, Firefox 20+ */
            justify-content: center;
            align-items: center;
            flex-wrap: wrap;
            margin-left: 0;
            list-style: none;
            & li {
                display: inline-block;
                padding: 2px 5px;
            }
        }
        & .social-icons {
            padding: 0;
            margin: 0;
            & a {
                width: 44px;
                height: 44px;
                &:not(:hover) {
                    background: #000000;
                }
                & svg {
                    fill: #ffffff;
