layouts/products/productView.scss:
// =============================================================================
// PRODUCT VIEW (CSS)
// =============================================================================
// Product view layout
// -----------------------------------------------------------------------------
.productView--quickView {
    .socialLinks-item--print {
        display: none;
    }
}

#relatedProducts.module-wrapper,
#similarByViews.module-wrapper,
#reviews-product.module-wrapper {
    border: 1px solid stencilColor('border-base-color');
    background-color: #ffffff;
    -o-border-radius: 5px;
    -ms-border-radius: 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    overflow: hidden;
    margin-bottom: 40px;
    @media (max-width: 991px) {
        margin-bottom: 25px;
    }
}

#reviews-product {
    .toggle-title {
        font-family: $fontFamily-headings;
        background: #ffffff;
        border-bottom: 1px solid stencilColor('border-base-color');
