        padding: 14px 30px 13px;
        margin-top: 0;
        margin-bottom: 0;
        font-size: 14px;
        letter-spacing: 0;
        text-align: left;
        font-weight: bold;
        color: #2f2f2f;
        line-height: 1.1;
        text-transform: uppercase;
        a.toggleLink span {
            color: #969696;
            font-size: 12px;
            text-transform: capitalize;
        }
        i.fa {
            font-size: 12px;
            color: #969696;
        }
    }
    .toggle-content {
        padding: 30px;
        &.is-open {
            display: block;
        }
    }
}

.productView {
