        display: none $important;

    }
    @else if $variant == "visually" {

        border: 0 $important;
        clip: rect(1px, 1px, 1px, 1px) $important;
        height: 1px $important;
        overflow: hidden $important;
        padding: 0 $important;
        position: absolute $important;
        width: 1px $important;
    }
}

// =============================================================================
// COLORS (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
//
// -----------------------------------------------------------------------------
// scss-lint:disable ImportantRule

@if $exportCSS--colorUtilities { // 1

    @each $color in map-keys($colorMap) {

        @each $variant in map-keys(map-get($colorMap, $color)) {

            .u-bg#{ colorUtilityClassName($color, $variant) } {
                background-color: color($color, $variant) !important;
            }

            .u-color#{ colorUtilityClassName($color, $variant) } {
                color: color($color, $variant) !important;
            }

        }

    }

}

// =============================================================================
// DISPLAY (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
// Adapted from SUIT CSS: https://github.com/suitcss/utils-display/
//
// 2. is-srOnly stands for "is screen reader only", meaning the content is not
// visible to users, but is read out by assistive technology like screen readers.
// Can be used to give greater context about a component when the visual treatment
// is minimal with no supportive text to describe it.
//
// -----------------------------------------------------------------------------

@if $exportCSS--displayUtilities { // 1

    .u-block {
        @include u-display("block", !important);
    }

    .u-inlineBlock {
        @include u-display("inlineBlock", !important);
    }

    .u-hidden {
        @include u-hidden("fully", !important);
    }

    .u-hiddenVisually {
        @include u-hidden("visually", !important);
    }

    .is-srOnly { // 2
        @include u-hidden("visually", !important);
    }

}

// =============================================================================
// LAYOUT (Tools)
// =============================================================================

@mixin u-clearFloats($important: null) {

    &:before,
    &:after {

        // ---------------------------------------------------------------------
        //
        // 1. The space content is one way to avoid an Opera bug when the
        //    'contenteditable' attribute is included anywhere else in the
        //    document. Otherwise it causes space to appear at the top and
        //    bottom of the element.
        // 2. The use of `table` rather than `block` is only necessary if using
        //    ':before' to contain the top-margins of child elements.
        //
        // ---------------------------------------------------------------------

        content: " " $important; // 1
        display: table $important; // 2
    }

    &:after {

        clear: both $important;
    }
}

// =============================================================================
// LAYOUT (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
// Adapted from SUIT CSS: https://github.com/suitcss/utils-layout/
//
// -----------------------------------------------------------------------------
// scss-lint:disable ImportantRule

@if $exportCSS--layoutUtilities { // 1

    .u-floatLeft {
        float: left !important;
    }

    .u-floatRight {
        float: right !important;
    }

    .u-clearFloats {
        @include u-clearFloats;
    }
}

// =============================================================================
// LISTS (Tools)
// =============================================================================


// List Bullets
// -----------------------------------------------------------------------------

@mixin u-listBullets($type, $important: null) {

    @if $type == "circle" {

        list-style-type: circle $important;

        ul {

            list-style: inherit $important;
        }

    }
    @else if $type == "disc" {

        list-style-type: disc $important;

        ul {

            list-style: inherit $important;
        }

    }
    @else if $type == "none" {

        list-style: none $important;
        margin-#{$default-float}: $list-side-margin-no-bullet $important;

        ul,
        ol {

            list-style: none $important;
            margin-bottom: 0 $important;
        }

    }
    @else if $type == "square" {

        list-style-type: square $important;

        ul {

            list-style: inherit $important;
        }

    }
}

//
// Horizontal Lists
// -----------------------------------------------------------------------------

@mixin u-listHorizontal() {
    @include u-listBullets("none");

    li {
        display: inline-block;
    }
}

//
// List Rest
//
// Purpose: A general helper to reset a list style of all styling
// -----------------------------------------------------------------------------

@mixin u-listReset() {
    @include u-listBullets("none");
    margin: 0;
    padding: 0;

    li {
        margin: 0;
        padding: 0;
    }
}

// =============================================================================
// LISTS (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
// Adapted from Foundation CSS:
//
// -----------------------------------------------------------------------------

@if $exportCSS--listUtilities { // 1

    .u-listCircleBullet {
        @include u-listBullets("circle", !important);
    }

    .u-listDiscBullet {
        @include u-listBullets("disc", !important);
    }

    .u-listNoBullet {
        @include u-listBullets("none", !important);
    }

    .u-listSquareBullet {
        @include u-listBullets("square", !important);
    }

    .u-listHorizontal {
        @include u-listHorizontal;
    }

    .u-listReset {
        @include u-listReset;
    }

}

// =============================================================================
// POSITION (Tools)
// =============================================================================


@mixin u-position($variant, $important: null) {

    @if $variant == "absoluteCenter" {

        // ---------------------------------------------------------------------
        //
        // Pins to all corners by default. But when a width and/or height are
        // provided, the element will be centered in its nearest
        // relatively-positioned ancestor.
        //
        // ---------------------------------------------------------------------

        bottom: 0 $important;
        left: 0 $important;
        margin: auto $important;
        position: absolute $important;
        right: 0 $important;
        top: 0 $important;

    }
    @else if $variant == "fixed" {

        // ---------------------------------------------------------------------
        //
        // Make sure fixed elements are promoted into a new layer, for
        // performance reasons.
        //
        // ---------------------------------------------------------------------

        backface-visibility: hidden $important;
        position: fixed $important;
    }
}

// =============================================================================
// POSITION (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
// Adapted from SUIT CSS: https://github.com/suitcss/utils-position/
//
// -----------------------------------------------------------------------------

@if $exportCSS--layoutUtilities { // 1

    .u-posAbsoluteCenter {
        @include u-position("absoluteCenter", !important);
    }

    .u-posFixed {
        @include u-position("fixed", !important);
    }

}

// =============================================================================
// TEXT (Tools)
// =============================================================================


// Text Kerning
// -----------------------------------------------------------------------------

@mixin u-textKern($important: null) {

    // -------------------------------------------------------------------------
    //
    // 1. Chrome (not Windows), Firefox, Safari 6+, iOS, Android
    // 2. Chrome (not Windows), Firefox, IE 10+
    // 3. Safari 7 and future browsers
    //
    // -------------------------------------------------------------------------

    text-rendering: optimizeLegibility $important; // 1
    font-feature-settings: "kern" 1 $important; // 2
    font-kerning: normal $important; // 3
}


// Text Lead
// -----------------------------------------------------------------------------

@mixin u-textLead($important: null) {

    line-height: 1.6 $important;
    font-size: 1.1em $important;
}


// Text Truncate
// -----------------------------------------------------------------------------

@mixin u-textTruncate($important: null) {

    // -------------------------------------------------------------------------
    //
    // 1. Ensure that the node has a maximum width after which truncation
    //    can occur.
    // 2. Fix for IE 8/9 if `word-wrap: break-word` is in effect on ancestor
    //    nodes.
    //
    // -------------------------------------------------------------------------

    max-width: 100% $important; // 1

    overflow: hidden $important;
    text-overflow: ellipsis $important;
    white-space: nowrap $important;
    word-wrap: normal $important; // 2
}


// Font Smoothing
// -----------------------------------------------------------------------------

@mixin u-fontSmoothing($important: null) {
    -moz-osx-font-smoothing: grayscale $important;
    -webkit-font-smoothing: antialiased $important;
}

// =============================================================================
// WIDTHS (Tools)
// =============================================================================


// Width Mixins
// -----------------------------------------------------------------------------

@mixin u-width($width, $important: null) {

    @if $width == "full" {

        border-left: 0 $important;
        border-right: 0 $important;
        margin-left: 0 $important;
        margin-right: 0 $important;
        padding-left: 0 $important;
        padding-right: 0 $important;
        width: 100% $important;

    }
    @else if $width == "fixed" {

        margin: auto $important;
        max-width: layout(fixed) $important;
        width: layout(max) $important;

    }
}

// =============================================================================
// TEXT (CSS)
// =============================================================================


// Bigcommerce Utility Classes
// -----------------------------------------------------------------------------
//
// 1. If set to false, there is no outputted CSS for this component.
// Adapted from SUIT CSS: https://github.com/suitcss/utils-text/
//
// -----------------------------------------------------------------------------
// scss-lint:disable ImportantRule

@if $exportCSS--textUtilities { // 1

    .u-textAlignCenter {
        text-align: center !important;
    }

    .u-textAlignLeft {
        text-align: left !important;
    }

    .u-textAlignRight {
        text-align: right !important;
    }

    .u-textBreak {
        word-wrap: break-word !important;
    }

    .u-textInheritColor {
        color: inherit !important;
    }

    .u-textKern {
        @include u-textKern(!important);
    }

    .u-textLead {
        @include u-textLead(!important);
    }

    .u-textNoWrap {
        white-space: nowrap !important;
    }

    .u-textTruncate {
        @include u-textTruncate(!important);
    }

    .u-fontSmoothing {
        @include u-fontSmoothing(!important);
    }
}

layouts/header/header.scss:
// =============================================================================
// HEADER (CSS)
// -----------------------------------------------------------------------------
.header {
    background-color: stencilColor('header-backgroundColor');
    border-bottom: 10px solid stencilColor('header-bottom-line-background');
    width: 100%;
    float: left;
}

.header-report {
    position: relative;
    background-color: stencilColor('topPagePromotion-backgroundColor');
    @media (max-width: 991px) {
        display: none;
    }
    .imgPromotion {
        display: block;
    }
    .close {
        position: absolute;
        top: 35%;
        right: 36px;
        line-height: 1;
        font-size: 25px;
        opacity: 1;
        color: stencilColor('topPagePromotion-closeIconColor');
        svg {
            width: 21px;
            height: 21px;
            fill: stencilColor('topPagePromotion-closeIconColor');
        }
        @media (max-width: 1340px) {
            right: 10px;
        }
        @media (max-width: 991px) {
            top: 23%;
        }
        @media (max-width: 800px) {
            display: none;
        }
    }
}

.header-bottom {
    padding: 10px 0 9px 0;
    height: 40px;
    overflow: hidden;
    background-color: stencilColor('headerBottom-backgroundColor');
    @media(max-width: 992px){
        padding: 10px 10px 9px 10px;
    }
    ul.list-top li.showMore {
        display: none;
        position: absolute;
        right: 0;
        top: 0;
        padding-left: 10px;
        margin-bottom: 0 !important;
        background: #fff;
        a {
            font-weight: 700 !important;
            border: 0;
        }
        &:hover {
            a {
                border-bottom: 0 !important;
            }
        }
    }
    &.is-open {
        height: auto;
        ul.list-top li.showMore {
            position: static;
        }
    }
    &.active {
        ul.list-top li.showMore {
            display: inline-block;
            margin-right: 20px;
            padding-right: 0;
        }
        ul.list-top {
            padding-left: 20px;
            padding-right: 60px;
        }
    }

    .title-top {
        color: stencilColor('header-bottom-text-color');
        font-size: 12px;
        font-weight: 700;
        font-family: stencilFontFamily('headings-font'), Arial, Helvetica, sans-serif;
        position: relative;
        text-transform: uppercase;
        margin-right: 41px;
        &:before {
            background: url(../img/list-sprite.png) 0 -209px no-repeat;
            position: absolute;
            content: "";
            width: 26px;
            height: 15px;
            top: 0px;
            right: -35px;
        }
    }
    ul.list-top {
        margin: 0;
        display: inline-block;
        li {
            list-style: none;
            display: inline-block;
            margin-bottom: 10px;
            &+li {
                margin-bottom: 10px;
                a {
                    border-right: 1px solid rgba(90, 90, 90, 0.30);
                }
            }
            &:last-child {
                border-right: 0;
            }
            a {
                color: stencilColor('header-bottom-text-color');
                font-size: 12px;
                font-weight: 400;
                line-height: 20px;
                padding-right: 8px;
                margin-right: 5px;
                font-family: stencilFontFamily('headings-font'), Arial, Helvetica, sans-serif;
                &:hover span {
                    color: stencilColor('header-bottom-text-color-hover');
                    border-bottom: 1px solid stencilColor('header-bottom-text-color-hover');
                }
            }
        }
    }
}

header.header {
    @media (max-width: 991px) {
        .container {
            width: 100%; 
            padding-left: 10px;
            padding-right: 10px;
        }
    }
}

.header-top {
    position: relative;
    background: stencilColor('header-top-backgroundColor');
    color: stencilColor('headerTop-textColor');
    & .container {
        position: static;
    }
    @media (max-width: 991px) {
        border-bottom: 1px solid rgba(0, 0, 0, 0.12);
        padding: 0 8px;
        background-color: #2874f0;
        .mobile-search-toggle {
            width: 42px;
            height: 100%;
            position: absolute;
            left: 0;
            top: 0;
            padding: 0;
            svg {
                width: 20px;
                height: 100%;
                padding: 3px;
                fill: stencilColor('headerTop-textColor');
            }
        }
    }
    & ul {
        margin: 0;
        & li {
            display: inline-block;
            list-style: none;
            padding: 0 14px;
            a {
                color: stencilColor('headerTop-textColor');
                display: inline-block;
                text-transform: capitalize;
                &:hover {
                    color: stencilColor('headerTop-textColorHover');
                    border-bottom: 1px stencilColor('headerTop-textColor');
