    font-size: 14px;
    letter-spacing: 0;
    font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
    font-weight: 400;
    color: #969696;
    line-height: 18px;

}

.modal-close {
    @include square($reveal-close-height);
    color: stencilColor("button--modal-close-color");
    width: 30px;
    height: 30px;
    position: absolute;
    right: -15px;
    top: -15px;
    font-size: 25px;
    padding: 0 0 1px;
    border-radius: 50%;
    color: white;
    text-align: center;
    text-decoration: none !important;
    z-index: zIndex("high");
    background: stencilColor("button--modal-close-bgColor");
    display: -webkit-box;
    /* OLD - iOS 6-, Safari 3.1-6 */
    display: -moz-box;
    /* OLD - Firefox 19- (buggy but mostly works) */
    display: -ms-flexbox;
    /* TWEENER - IE 10 */
    display: -webkit-flex;
    /* NEW - Chrome */
    display: flex;
    /* NEW, Spec - Opera 12.1, Firefox 20+ */
    justify-content: center;
    align-items: center;
    &:hover {
        color: stencilColor("button--modal-close-colorHover");
         text-decoration: none;
         background: stencilColor("button--modal-close-bgColorHover");
    }
}

.modal-body {
    overflow: auto;
    -webkit-overflow-scrolling: touch;
    padding: $reveal-modal-padding;
