components/stencil/blocker/blocker.scss:
// =============================================================================
// BLOCKER (CSS)
//
// Purpose: Position .blocker element above another element to prevent user event
// =============================================================================


.blocker {
    background: rgba($body-bg, 0.5);
    bottom: 0;
    left: 0;
    position: absolute;
    right: 0;
    top: 0;
    z-index: zIndex("highest");
}

.blocker-container {
    position: relative;
}

components/stencil/compare/compare.scss:
// =============================================================================
// COMPARE (CSS)
// =============================================================================


// Compare table layout
// -----------------------------------------------------------------------------
//
// 1. Foundation Magellan plugin.
//
// -----------------------------------------------------------------------------
.product-list{
    margin:0 -15px;
    overflow:hidden;
    list-style:none;
    .product-card{
        width:20%;
        display:inline-block;
        padding:0 15px 30px;
        margin:0 -1.5px;
        vertical-align:top;
        text-align: center;
        @media (max-width: 991px) {
            width: 25%;
        }
        @media (max-width: 767px) {
            width: 33.33%;
        }
        @media (max-width: 580px) {
            width: 50%;
        }
        @media (max-width: 380px) {
           padding:0 10px 30px;
           margin:0 -1.0px;
        }
        .card{
            margin-bottom:20px;
            .compareTable-removeProduct{                
                height: spacing("single") + spacing("base");
                position: absolute;
                right:0;
                top:0;
                transition: background-color 0.15s ease;
                width: spacing("single") + spacing("base");
                text-align: center;
                z-index:10;
                .icon {
                    fill: stencilColor("icon-color");
                    height: spacing("single");
                    margin-top: spacing("third");
                    width: spacing("single");
                }
                &:hover{
                    .icon{
                        fill: stencilColor('color-other-textLink--hover');
                    }
                }
            }
        }
        .card-body{
            text-align:center;
        }
        .card-text{
            margin-bottom: 10px;
            overflow: hidden;
            min-height: 18px;
            a{
                color:#a5a5a5;
            }
        }
        .card-title{
            margin-top: 0px;
            margin-bottom: 7px;
            font-size: fontSize("base");
            font-family: stencilFontFamily('card-title-font');
            font-weight: 400;
            line-height: 20px;
            color: stencilColor('card-title-color');
            letter-spacing: 0;
            &:hover a {
                color: stencilColor('card-title-color-hover');
                text-decoration: none;
            }
        }
        .prod-brand{
            margin-bottom: 7px;
            text-transform:uppercase;
            font-weight:600;
            font-family: stencilFontFamily("body-secondary-font"), Arial, Helvetica, sans-serif;
            color:stencilColor('brand-name-color');
            min-height:20px;
            & a {
                color:stencilColor('brand-name-color');
            }
        }
        .prod-price {
            color: stencilColor('basic-price-color');
            font-family: stencilFontFamily('card-title-font');
            font-size: $fontSize-base;
            line-height: 25px;
            margin-bottom: 10px;
            font-weight: 400;
            font-size: 16px;
            & .price--rrp {
                color: stencilColor('retail-price-color');
                margin-right: 8px;
                font-size: 14px;
                font-style: italic;
                letter-spacing: 0;
                & ~ .price--withoutTax {
                    color: stencilColor('sale-price-color');
                    font-weight: 400;
                    letter-spacing: 0;
                }
            }
            p[translate] {
                color: $anchor-font-color-hover;
                font-size: $fontSize-base;
                margin-bottom: 10px;
            }
        }
        .prod-summary{
            display:none;
        }
        .star-rating,
        .availability{
            margin-bottom:10px;
        }
        & > .btn{
            display:block;
            margin:10px 0 12px;
            min-height: 40px;
            line-height: 38px;
            padding: 0 25px;
            @media (max-width: 380px) {
                padding: 0 10px;
            }
        }
        & ul {
            & li {
                list-style: none;
            }
        }
    }
    &.product-list-2{
        .product-card{
            width:50%;
        }
    }
    &.product-list-3{
        .product-card{
            width:33%;
        }
    }
    &.product-list-4{
        .product-card{
            width:25%;
        }
    }
}

components/stencil/toggleLink/toggleLink:
// =============================================================================
// TOGGLE LINK (CSS)
// =============================================================================


// Toggle link
// -----------------------------------------------------------------------------

$toggle-spacing: spacing("single") + spacing("half");

.toggle {
    border-top: container("border");
    margin-bottom: $toggle-spacing;

    &:last-of-type {
        margin-bottom: $toggle-spacing * 2;
    }
}

.toggle-title {
    margin-bottom: $toggle-spacing;
}

.toggleLink {
    color: stencilColor("color-textSecondary");

    &:hover {
        color: stencilColor("color-textSecondary--hover");
    }

    .toggle-title & {
        float: right;
        line-height: 24px;
    }
}

.toggleLink-text {
    font-size: fontSize("base");
    font-weight: fontWeight("normal");
    i{
        margin-left: 10px;
    }
}

.toggleLink-text--off {
    display: inline-block;

    .toggleLink.is-open & {
        display: none;
    }
}

.toggleLink-text--on {
    display: none;

    .toggleLink.is-open & {
        display: inline-block;
    }
}

.toggle-content {
    display: none;

    &.is-open {
        display: block;
    }
}

components/stencil/facetedSearch/faceted:
// =============================================================================
// FACET SEARCH (CSS)
// =============================================================================
// Faceted search toggle
// -----------------------------------------------------------------------------
//
// 1. Do not show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-toggle {
    margin-bottom: 30px;
    clear: both;
    -webkit-box-shadow: 0 2px 5px rgba(0,0,0,.08);
    -moz-box-shadow: 0 2px 5px rgba(0,0,0,.08);
    box-shadow: 0 2px 5px rgba(0,0,0,.08);
    border-bottom: 1px solid #dcdcdc;
    background-color: #ffffff;
    border-radius: 3px;
    overflow: hidden;
    
    @include grid-row;
    // border: $facetedSearch-toggle-border;
    // border-radius: $facetedSearch-toggle-borderRadius;
    // border-width: $facetedSearch-toggle-borderWidth;
    display: block;
    // margin-bottom: $facetedSearch-toggle-verticalSpacing;
    // padding: $facetedSearch-toggle-padding;
    padding: 14px 20px;
    @include breakpoint("medium") {
        // scss-lint:disable ImportantRule
        display: none !important; // 1
    }
    &.is-open {
        /* border-bottom-left-radius: 0; */
        /* border-bottom-right-radius: 0; */
        margin-bottom: 20px;
    }
}

.facetedSearch-toggle-text,
.facetedSearch-toggle-indicator {
    display: block;
    font-weight: normal;
}

.facetedSearch-toggle-text {
    @include grid-column(7);
    padding: 0;
    text-align: left;
}

.facetedSearch-toggle-indicator {
    @include grid-column(5);
    font-weight: bold;
    padding: 0;
    text-align: right;
}

// Faceted search nav list
// -----------------------------------------------------------------------------
//
// 1. Do not show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-navList {
    display: none;
    @include breakpoint("medium") {
        // scss-lint:disable ImportantRule
        display: block !important; // 1
    }
    &.is-open {
        display: block;
        // scss-lint:disable SelectorDepth, NestingDepth
        .accordion--navList,
        .accordion-block:first-child .accordion-navigation {
            border-top-left-radius: 0;
            border-top-right-radius: 0;
        }
    }
    .facetedSearch-toggle + &,
    .sidebarBlock + & {
        margin-top: 20px;
    }
    .facetedSearch-toggle + &.is-open,
    .sidebarBlock + &.is-open {
        margin-top: -1px;
        @include breakpoint("medium") {
            margin-top: $facetedSearch-toggle-verticalSpacing;
        }
    }
    .navList--inner {
        margin-left: spacing("single");
    }
    .accordion--navList .accordion-navigation-actions {
            top: 14px;
            right: 20px;
    }
    @media (max-width: 767px) {
        
        .accordion--navList .accordion-navigation-actions {
            top: 14px;
            right: 20px;
        }
    }
    .accordion--navList .accordion-indicator {
        visibility: visible;
    }
    & a.toggleLink {
        display: inline-block;
        margin-top: 10px;
        & span.toggleLink-text {
            font-size: 14px;
            font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
        }
    }
}

.facet-quick-heading {
    text-transform: capitalize;
}

.facetedSearch-optionColumns {
    column-count: 3;
    column-gap: 20px;
}

// Faceted search refine filters
// -----------------------------------------------------------------------------
//
// 1. Only show if viewport is over "medium" breakpoint
//
// -----------------------------------------------------------------------------
.facetedSearch-refineFilters {
    display: none;
    @include breakpoint("medium") {
        display: block; // 1
    }
    & ul.inlineList {
        margin-bottom: 10px;
    }
}

#facetedSearch-navList .accordion-content > ul.navList li a {
    border: 0 !important;
    font-size: 12px;
    line-height: 30px;
    font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
    color: $anchor-font-color;
    &:hover {
        color: $anchor-font-color-hover;
    }
}

#facetedSearch-navList .accordion-content > ul.navList li a::before {
    position: static;
    display: inline-block;
    vertical-align: -3px;
    margin-right: 3px;
}

// #facetedSearch-navList .accordion-content > ul.navList li a:hover span:not(.navList-action-close) {
//     border-bottom: 1px solid $anchor-font-color-hover;
// }

#facetedSearch-navList .accordion--navList .accordion-block {
    margin: 0 0 20px;
    clear: both;
    padding-top: 1px;
    .pad-block{
        padding-bottom: 20px;
    }
    &:last-of-type {
        margin-bottom: 0;
    }
    @media (max-width: 991px) {
        margin-bottom: 20px;
    }
}

#facetedSearch-navList .form-minMaxRow {
    margin-bottom: 15px;
    & .form-field {
        width: 50%;
    }
}

#facetedSearch-navList .rating--small {
    display: inline;
}

#facetedSearch {
    margin-bottom: 20px;
}

components/stencil/facetLabel/facetLabel:
// =============================================================================
// FACET LABEL (CSS)
// =============================================================================

.facetLabel {
    background: $facetLabel-backgroundColor;
