components/stencil/addressBox/addressBox:
// =============================================================================
// ADDRESS BOX (CSS)
// =============================================================================

// Address layout
// -----------------------------------------------------------------------------
//
// 1. Using display: inline-block; to vertically align blocks.
//    Font-size gets redefined under the .address block.
//
// -----------------------------------------------------------------------------

.addressList {
    font-size: 0; // 1
    list-style: none;
    margin-left: -(spacing("half"));
    margin-right: -(spacing("half"));
    .form-actions{
        a.btn{
            margin-right: 12px;
        }
    }
}

.address {
    display: inline-block;
    padding: 0 spacing("half");
    vertical-align: top;
    width: 100%;

    @include breakpoint("small") {
        width: grid-calc(6, $total-columns);
    }

    @include breakpoint("large") {
        width: grid-calc(4, $total-columns);
    }
}


// Address
// -----------------------------------------------------------------------------
//
// 1. Redefine the font-size as we're removing white-space on the parent by
//    reducing the size to 0.
// 2. Adding a min-height to improve the consistency of the address blocks.
// 3. On larger screens we position: absolute; the .form-actions so they're
//    consistently in line. We need to adjust padding to accommodate this.
//
// -----------------------------------------------------------------------------

.panel--address {
    font-size: fontSize("base"); // 1
    position: relative;  

    .panel-body {
        min-height: remCalc(250px); // 2
        padding: spacing("single");
        background-color: transparent;
        border: container("border");
        display: block;
        border-radius: 3px;
        -webkit-border-radius: 3px;
        -moz-border-radius: 3px;

        @include breakpoint("small") {
            padding: spacing("single") spacing("single") (spacing("single") + spacing("double")); // 3
        }
    }

    .form-actions {
        margin-bottom: -(spacing("half"));
        margin-top: spacing("single");
        text-align: left;

        @include breakpoint("small") { // 3
            bottom: spacing("single");
            position: absolute;
            width: auto;
        }

        // scss-lint:disable SelectorDepth, NestingDepth
        .button {
            margin-bottom: spacing("half");

            + .button {
                margin-left: 0;
                margin-top: 0;

                @include breakpoint("small") {
                    margin-left: spacing("quarter");
                }
            }
        }
        // scss-lint:enable SelectorDepth, NestingDepth
    }
}

.address-title {
    margin: 0 0 spacing("half");
    text-transform: none;
    font-size: 14px;
    font-weight: 700;
    text-transform: capitalize;
}

.address-details {
    list-style: none;
    margin: 0 0 spacing("half");
}

.address-details--postal {
    font-size: fontSize("base");
    color: stencilColor("color-textSecondary");
}

.address-label,
.address-description {
    display: inline-block;
    font-size: fontSize("base");
    margin: 0;
}

.address-label {
    color: stencilColor("color-textSecondary");
    font-weight: fontWeight("normal");
}

.panel--newAddress {
    display: block;
    text-align: center;
    text-decoration: none;

    &:hover {

        // scss-lint:disable SelectorDepth, NestingDepth
        .address-title {
            color: stencilColor("color-textLink--hover");
        }
        // scss-lint:enable SelectorDepth, NestingDepth
    }

    .panel-body {
        background-color: transparent;
        border: container("border");
        display: block;
        border-radius: 3px;
        -webkit-border-radius: 3px;
        -moz-border-radius: 3px;
    }

    .address-title {
        margin-bottom: 0;
        transition: all 0.15s ease;
    }
}

.address-addNew {
    left: 50%;
    position: absolute;
    top: 50%;
    transform: translateX(-50%) translateY(-55%);
}

.address-symbol {
    font-size: fontSize("hero");
    line-height: 1;
    svg{
        width: 64px;
        height: 64px;
        fill: #9c9c9c; 
        margin-bottom: 16px;
    }
    .address-title{
        text-transform: uppercase;
        line-height: 1.3;
        border-bottom: 1px dotted;
        font-size: 12px;
    }
}

components/stencil/maintenanceNotice/mai:
// =============================================================================
// MAINTENANCE MODE (CSS)
// =============================================================================

.maintenanceNotice {
    background-color: $maintenanceNotice-backgroundColor;
    box-shadow: $maintenanceNotice-box-shadow;
    color: $maintenanceNotice-color;
    left: $maintenanceNotice-left;
    padding: $maintenanceNotice-padding;
    position: fixed;
    top: $maintenanceNotice-top;
    width: $maintenanceNotice-width;
    z-index: $maintenanceNotice-z-index;

    > :last-child {
        margin-bottom: 0;
    }

    a {
        color: $maintenanceNotice-color;
    }
}

.maintenanceNotice-header {
    font-family: $maintenanceNotice-header-font-family;
    margin: $maintenanceNotice-header-margin;
}

components/stencil/videoGallery/videoGal:
// =============================================================================
// VIDEO GALLERY
// =============================================================================

.videoGallery-main {
    @include flex-video-container();
    margin-bottom: $videoGallery-spacing;
}

.videoGallery-list {
    @include u-listBullets("none");
    @include grid-row($behavior: "nest");
    font-size: 0;
}

.videoGallery-item {
    @include grid-column(12);
    display: inline-block;
    float: none;
    vertical-align: top;

    @include breakpoint("medium") {
        width: grid-calc(6, $total-columns);
    }
}

.video {
    @include media;
    display: block;
    margin-bottom: $videoGallery-spacing;
    opacity: 0.4;
    text-decoration: none;
    transition: opacity 100ms ease-out;

    @include breakpoint("medium") {
        margin-bottom: $videoGallery-spacing;
    }

    &:hover,
    &.is-active {
        color: color("greys", "darker");
        opacity: 1;
    }
}

.video-figure {
    margin-right: $videoGallery-spacing;
}

.video-title {
    margin-bottom: 0;
    margin-top: 0;
    text-transform: none;
    line-height: 20px;
    letter-spacing: 0;
    font-size: fontSize("base");
    font-family: $fontFamily-sans;
    font-weight: 700;
}

.video-description {
    font-size: fontSize("base");
    margin-bottom: 0;
}

components/stencil/previewCart/previewCa:
// =============================================================================
// PREVIEW CART (CSS)
// =============================================================================
//
// 1. Override server-generated inline-css
//
// -----------------------------------------------------------------------------
#previewModal {
    .modal-content {
        position: relative;
    }
    @media (min-width: 767px) {
        &:before {
            content: '';
            position: absolute;
            right: 0;
            top: 0;
            width: 300px;
            height: 100%;
            background-color: #f5f5f5;
            z-index: 1;
        }
        .modal-body {
            padding: 0;
        }
    }
    @media (min-width: 768px) and (max-width: 991px) {
        max-width: 768px;
    }
}

.previewCart {
    #mayAlsoLike {
        width: calc(100% - 285px);
        float: left;
        @media (max-width: 991px) {
            display: block;
        }
        @media (max-width: 767px) {
            width: 100%;
            display: block;
            padding-left: 0;
            padding-right: 0;
        }
        & .productCarousel {
            padding: 30px 30px 60px 30px;
            @media (max-width: 767px) {
                padding: 0;
            }
        }
        &>h3 {
            font-family: $fontFamily-headings;
            background: #ffffff;
            padding-top: 0;
            border-top: 1px solid #eaeaea;
            padding: 35px 30px 17px;            
            margin: 0 30px;
            padding-left: 0;
            font-size: 14px;
            letter-spacing: 0;
            text-align: left;
            font-weight: bold;
            color: #2f2f2f;
            line-height: 1.1;
            text-transform: uppercase;
             @media (max-width: 767px) {
               margin: 0;
             }
        }

        & .owl-nav>div {
            top: 25%;
            background: #5b5b5b;
            border: 0;
            width: 32px;
            height: 32px;
            -webkit-border-radius: 3px;
            -moz-border-radius: 3px;
            border-radius: 3px;
            &:hover {
                background: #000000;
            }
            &.owl-prev {
                right: 70px;
                left: auto;
                top: -27px;
                @media (max-width: 767px){
                  right: 40px;
                }
                &::before {
                    content: '\f053';
                    color: #fff;
                    font-size: 14px;
                }
            }
            &.owl-next {
                right: 30px;
                left: auto;
                top: -27px;
                @media (max-width: 767px){
                  right: 0;
                }
                &::before {
                    content: '\f054';
                    color: #fff;
                    font-size: 14px;
                }
            }
        }
    }
    .productView {
        @include grid-column(8);
          display: flex;
          margin: 0;
          padding-bottom: 0;
          padding-left: 30px;
          padding-top: 30px !important;
          @media (max-width: 991px) {
             width: 60%;
             display: block;
          }
          @media (max-width: 767px) {
             width: 100%;
             display: block;
             padding-left: 0;
             padding-right: 0;
          }
    }
    .productView-image {
        @include grid-column(5);
        @media (max-width: 991px) {
            float: none;
            margin: 0 auto;
            max-width: 210px;
            padding-bottom: 15px;
        }
    }
    .productView-image--cart {
        align-self: flex-start;
        margin: 0 auto;
        width: auto;
    }
    .productView-details {
        @include grid-column(7);
        @media (max-width: 991px) {
            padding-left: 0;
            padding-right: 0;
            width: 100%;
            float: none;
            margin: 0;
        }
    }
    .productView-brand {
        margin-bottom: 15px !important;
        font-size: 11px !important;
    }
    .productView-title {
        margin-top: 0;
        font-size: 13px;
        ;
        font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
        font-weight: 600;
        margin: -4px 10px 14px 0;
        letter-spacing: 0;
        line-height: 22px;
        color: #228dd6;
        @media (max-width: 767px) {
            margin-top: 0;
            padding-top: 5px;
        }
    }
    .productView-info {
        padding-bottom: 7px;
        & .productView-info-name {
            margin-bottom: 0;
            margin-right: 30px;
            text-transform: capitalize;
            font-weight: 600;
            min-width: 100px;
        }
        & .productView-info-value {
            margin-bottom: 0;
            color: #646464;
        }
    }
    .productView-price {
        font-family: stencilFontFamily('card-title-font');
        font-size: 13px;
        margin-bottom: 20px;
        & span {
            font-weight: 600;
            color: #8c8c8c;
        }
    }
}

.previewCartCheckout {
     text-align: center;
   position: relative;
   z-index: 2;
    @media (min-width: 768px) {
        @include grid-column(4, $float: right);
        padding: spacing("single");
        max-width: 240px;
        width: 100%;
        margin: 0 30px 50px 30px;
        padding: 0;
    }
    p[data-cart-quantity] {
        margin-bottom: 15px;
        padding-top: 15px;
        color: #646464;
        border-top: 1px solid #eaeaea;
    }
    .btn {
        margin: 0;
        width: 100%;
        max-width: 100%;
        height: 40px; // scss-lint:disable NestingDepth
        +.btn,
        +p {
            margin-top: 15px;
        }
        &.btn-lg {
            height: 48px;
        }
        @media (max-width: 360px) {
            font-size: 11px;
        }
    }
    
    @media (max-width: 767px) {
        margin-bottom: 15px;
        position: relative;
    }
}

.previewCartCheckout-price {
    display: block;
    font-size: 24px;
    margin-top: 10px;
    font-weight: bold;
    text-align: center;
}

.previewCartCheckout-subtotal {
    display: block;
    margin-bottom: spacing("single");
    margin-top: spacing("single");
    padding-top: 15px;
    border-top: 1px solid #eaeaea;
    font-weight: 700;
    text-transform: uppercase;
}

.previewCartCheckout-additionalCheckoutButtons {
    p {
        float: none !important;
        margin: 15px 0 15px 0;
        font-size: 12px;
        font-weight: 400;
        color: #646464;
    }
}

.suggestiveCart {
    display: none;
    text-align: center;
    @include breakpoint("medium") {
        display: block;
    }
}

components/stencil/applePay/applePay.scs:
.apple-pay-checkout-button {
    background-position: 50% 50%;
    background-repeat: no-repeat;
    background-size: 100% 60%;
    border-radius: $applePay-borderRadius;
    cursor: pointer;
    display: none;
    max-height: remCalc(64px);
    min-height: remCalc(32px);
    min-width: remCalc(134px);
    padding: spacing("single");
    @if stencilString("applePay-button") == "white" {
        background-color: $applePay-white;
        background-image: -webkit-named-image(apple-pay-logo-black);
    }
    @else if stencilString("applePay-button") == "white-border" {
        background-color: $applePay-white;
        background-image: -webkit-named-image(apple-pay-logo-black);
        border: 0.5px solid $applePay-black;
    }
    @else {
        background-color: $applePay-black;
        background-image: -webkit-named-image(apple-pay-logo-white);
    }
}

.cart-additionalCheckoutButtons {
    .apple-pay-checkout-button {
        margin-top: spacing("half");
    }
}

.apple-pay-supported {
    .apple-pay-checkout-button {
        display: block;

        @include breakpoint("small") {
            display: inline-block;
            float: right;
        }
    }
}

.previewCartCheckout {
    .apple-pay-checkout-button {
        display: block;
        float: none;
        margin-top: spacing("half");
    }
}

components/stencil/cookie/cookie.scss:
.cookieMessage {
    background: color("greys", "lightest");
    color: color("greys", "darker");
    display: none;

    .button {
        width: grid-calc(12, $total-columns);

        @include breakpoint("medium") {
            vertical-align: middle;
            width: grid-calc(3, $total-columns);
        }

        @include breakpoint("large") {
            vertical-align: top;
        }
    }
}

.cookieMessage-container {
    margin: 0 auto;
    padding: spacing("single");

    @include breakpoint("medium") {
        width: grid-calc(10, $total-columns);
    }

    @include breakpoint("large") {
        width: grid-calc(7, $total-columns);
    }
}

.cookieMessage-text {
    @include breakpoint("medium") {
        display: inline-block;
        padding: 0 spacing("half");
        vertical-align: middle;
        width: grid-calc(8, $total-columns);
    }
}

components/stencil/rating/rating.scss:
// =============================================================================
// RATING (CSS)
// =============================================================================

.fa.fa-star {
    color: stencilColor("icon-ratingEmpty");
}

.fa.fa-star.color {
    color: stencilColor("icon-ratingFull");
}


// Small rating
// -----------------------------------------------------------------------------
//
// 1. Vertically aligns stars to container. Icons are off by default.
//    Icons are all vertically aligned to middle, however middle is not middle,
//    and therefore need to be adjusted to actually sit at middle.
//
// -----------------------------------------------------------------------------

.rating--small {
    display: inline-block;

    i.fa {
        font-size: 14px;
    }
}

components/stencil/productReviews/produc:
// =============================================================================
// PRODUCT REVIEWS (CSS)
// =============================================================================
// Review list
//
// 1. Kill extra display inline-block spacing
// -----------------------------------------------------------------------------
.productReviews-list {
   @include u-listBullets("none");
   font-size: 0; // 1
   margin-left: 0 !important;
}

// Review item
// -----------------------------------------------------------------------------
.productReview {
   display: block;
   float: none;
   font-size: fontSize("base");
   vertical-align: top;
   margin-bottom: 30px;
   width: 100%;
}

.productReview-title {
   margin: 0;
   text-transform: none;
}

.productReview-author {
   color: stencilColor("color-textSecondary");
   margin-bottom: spacing("base");
}

.productReview-rating {
   display: inline-block;
   margin-bottom: spacing("quarter");
}

.productReview-ratingNumber {
   @include u-hidden("visually");
}

#productReviews-content .pagination-list {
    margin-bottom: 30px;
   & li {
      padding-left: 0;
      padding-right: 0;
      & a {
         padding-left: 0;
         padding-right: 0;
         text-align: center;
      }
      & + li {
        margin-left: 20px;
      }
   }
}

