components/citadel/forms/forms.scss:
// =============================================================================
// FORMS (CSS)
// =============================================================================


// Form variants
// -----------------------------------------------------------------------------

.form--hiddenLabels {

    .form-label {
        @include u-hidden("visually");
    }
}
.form-label {
    font-weight: 700;
    font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
    font-size: 12px;
}

.alert {
    padding: 11px;
    padding-left: 27px;
}
// Citadel inline messages
// -----------------------------------------------------------------------------

.form-inlineMessage {
    display: block;
}


// Citadel field states
// -----------------------------------------------------------------------------

.giftCertificate-block {
    .form-label {
        float: left;
        width: 200px;
        @media (max-width: 600px) {
            width: 100%;
            margin-bottom: 15px;
            .note_optional {
                margin-left: 0;
                display: inline-block;
            }
            .form-actions {
                margin-left: 0;
            }
        }
    }
    @media (min-width: 600px) {
        .form-field {
            margin-bottom: 15px;
            display: flex;
            justify-content: left;
            align-items: center;
        }
        
    }
    @media (min-width: 880px) {
    .form-actions {
            margin-left: 150px;
            margin-top: 15px;
        }
    }
}
.form-radio-group {
    max-width: 378px;
    width: 100%;
    float: left;
    .form-label {
        width: 100%;
        margin-bottom: 5px;
    }
}

.form-textarea {
    textarea {
        width: 100%;
        border-radius: 3px;
        padding: 0.75rem 1rem;
        border-color: stencilColor('textarea-border-color');
        &:focus {
            border-color: stencilColor('textarea-border-color-active');
        }
    }
    max-width: 378px;
    width: 100%;
    float: left;
    @media (min-width: 991px) {
        max-width: 545px;
    }
}

.form-field-group {
    max-width: 378px;
    width: 100%;
    float: left;
}

.form-input-item {
    margin-bottom: 5px;
}

.form-input-group {
    max-width: 378px;
    width: 100%;
    float: left;
    .form-input {
        width: 100%;
    }
    .form-inlineMessage {
        margin-left: 0;
    }
}

.note_optional {
    color: #464646;
    opacity: 0.6;
    font-weight: 400;
    display: block;
}

.note_input {
    color: #464646;
    opacity: 0.6;
    clear: both;
    font-style: italic;
    margin-bottom: 0;
}

.form-check {
    width: 100%;
    margin-top: 36px;
    .form-label {
        float: none;
        width: 100%;
    }
    .form-inlineMessage {
        margin-left: 0;
    }
}

* &::-webkit-input-placeholder {
    /* Chrome/Opera/Safari */
    opacity: 1;
    position: relative;
    left: 0;
    transition: all .5s ease;
    -moz-transition: all .5s ease;
    -webkit-transition: all .5s ease;
    visibility: visible;
}

* &::-moz-placeholder {
    /* Firefox 19+ */
    opacity: 1;
    position: relative;
    left: 0;
    transition: all .5s ease;
    -moz-transition: all .5s ease;
    -webkit-transition: all .5s ease;
    visibility: visible;
}

* &:-ms-input-placeholder {
    /* IE 10+ */
    opacity: 1;
    position: relative;
    left: 0;
    transition: all .5s ease;
    -moz-transition: all .5s ease;
    -webkit-transition: all .5s ease;
    visibility: visible;
}

* &:-moz-placeholder {
    /* Firefox 18- */
    opacity: 1;
    position: relative;
    left: 0;
    transition: all .5s ease;
    -moz-transition: all .5s ease;
    -webkit-transition: all .5s ease;
    visibility: visible;
}

* &:focus::-webkit-input-placeholder {
    opacity: 0;
    left: 5%;
    visibility: hidden;
}

.form-field--error {

    .form-input {
        float: none;
    }

    .form-inlineMessage {

        // scss-lint:disable NestingDepth
        &:before {
            background: $form-error-icon no-repeat;
            background-size: $form-inlineMessage-backgroundSize;
            content: "";
            display: inline-block;
            height: $form-error-icon-height;
            left: $form-error-icon-left;
            margin-right: $form-error-icon-marginRight;
            position: relative;
            top: $form-error-icon-top;
            width: $form-error-icon-width;
        }
    }
}

.form-field--success {

    .form-input[type="text"] {
        background: {
            image: $form-success-icon;
            position: right $form-success-icon-right center;
            repeat: no-repeat;
        }
        padding-right: $form-success-input-paddingRight;
    }
}


// Citadel checkbox/radio positioning
// -----------------------------------------------------------------------------

.form-checkbox,
.form-radio {

    + .form-label {
        font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
        margin-bottom: 11px;
        // scss-lint:disable NestingDepth
        &:last-child {
            display: block;
            margin-bottom: $form-checkRadio-label-last-marginBottom;
        }

        &::after {
            top: 2px;
        }

        &::before {
            top: 1px;
        }
    }
}

.form-radio {
    &::before {
            border-radius: 50%;
        }
}
// Citadel form-actions
// -----------------------------------------------------------------------------
.gift-certificate-form {
    .form-actions {
        margin-top: 27px;
        padding-top: 30px;
        .btn {
            height: 40px;
        }
    }
}
form div.form-actions {
    margin-top: 3px !important;    
}
.form-actions {
    @include grid-row();
    display: block;

    @include breakpoint("small") {
        margin-top: spacing("single");
    }

    .button,
    input,
    a {
        vertical-align: top;
        width: 100%;

        @include breakpoint("small") {
            margin-bottom: 0;
            width: auto;
        }

        // scss-lint:disable NestingDepth
        + .button,
        + input,
        + a {
            margin-top: spacing("half");

            @include breakpoint("small") {
                margin: 0 0 0 spacing("half");
            }
        }
        // scss-lint:enable NestingDepth
    }
}

// Stencil form additions
// -----------------------------------------------------------------------------
//
// 1. Order has been placed here so that if a form error is shown, it will
//    appear before the button.
//
// -----------------------------------------------------------------------------

.form-label--alternate {
    font-weight: 700;
    margin-bottom: spacing("half");
    color: #000000;
    font-size: 12px;
    font-family: stencilFontFamily('body-secondary-font'), Arial, Helvetica, sans-serif;
}

.form-select--date {
    width: initial;
    margin-right: spacing("quarter");
    margin-bottom: spacing("quarter");
    &:last-of-type {
        margin-right: 0;
    }
}

.form-select--small,
.form-select--date {
    background-position: right;
    background-size: auto;
    font-size: fontSize("base");
    height: $input-small-height;
    line-height: 1rem;
    max-width: 20rem;
    padding: $input-small-padding;
    padding-right: $select-small-paddingRight;
    min-width: 90px;
}

.form-select--short {
    max-width: 6rem;
    width: 4.5rem;
}

.form-file {
    margin-bottom: spacing("third");
}

.form-fileDescription {
    color: color("greys", "light");
    font-size: fontSize("smallest");
}

.form-field-group {
    border: $input-border-width $input-border-style $input-border-color;
    border-radius: $input-border-radius;
    padding: spacing("single");
}

// This component should live inside a .form-row
.form-column {
    @include grid-column(12);

    @include breakpoint("small") {
        @include grid-column(6);
    }
}
.page-type-404{
    .form-prefixPostfix{
        max-width: 770px;
        margin: auto;
        @media (max-width: 480px){
            display: block;
        }
    }
}
.form-prefixPostfix {

    &.wrap {
        flex-wrap: wrap;
        width: 100%;
    }

    .form-input,
    .button {
        display: block;
        width: 100%;
    }

    .form-input {
        @include breakpoint("large") {
            width: auto;
        }
    }

    .button {
        @include breakpoint("medium") {
            width: auto;
        }
    }

    .form-inlineMessage {
        margin-bottom: spacing("quarter");

        @include breakpoint("large") {
            margin-bottom: 0;
        }
    }
}

.form-prefixPostfix-button--postfix {
    margin:  0 0 0 spacing("half");
    order: 1; // 1
    height: 36px;
    @media (max-width: 480px){
        margin: 0;
        margin-top: 20px;
    }

    @include breakpoint("large") {
        margin: 0 0 0 spacing("half");
        order: 0; // 1
    }
}
.form-prefixPostfix .form-input {
    -ms-flex: 1;
    flex: 1;
    @media (max-width: 480px){
        -ms-flex: none;
        flex: none;

    }
}

// Increment field
// -----------------------------------------------------------------------------

.form-increment {
    .button {
        margin: 0;
        padding: remCalc(3px) remCalc(5px);
        width: 36px;
        height: 36px;
        border-color: #cbcbcb;
        font-size: 12px;
        color: #7b7b7b;
        padding-top: 4px;
        &[data-action="dec"] {
            border-top-right-radius: 0;
            border-bottom-right-radius: 0;
         }
         &[data-action="inc"] {
            border-top-left-radius: 0;
            border-bottom-left-radius: 0;
         }
        &:hover {
            border-color: #000;
        }
    }
}

.form-input--incrementTotal {
    border: 0;
    display: inline-block;
    font-size: fontSize("base");
    height: auto;
    padding: 0;
    text-align: center;
    vertical-align: middle;
    width: remCalc(35px);
    min-width: 50px;
    height: 36px;
    border: 1px solid #cbcbcb;
    border-width: 1px 0;
    border-radius: 0;
    margin-left: -1px;
    &:disabled {
        border: transparent;
    }
}


// Stencil form options
// -----------------------------------------------------------------------------
//
// 1. Issue with FastClick JS library workaround: https://github.com/ftlabs/fastclick/issues/351
// 2. Resetting font due to inline-block whitespace issue.
//
/*[data-product-attribute="swatch"],
[data-product-attribute="set-rectangle"] {
    margin-bottom: 0 !important;
}*/
[data-product-attribute="swatch"] .form-option {
    width: 36px;
    height: 36px;

    & > span {
        position: relative;
    }

}
[data-product-attribute="swatch"] .form-radio:checked + .form-option  & > span{
    -webkit-box-shadow: inset 0px 0px 0px 2px rgba(0,0,0,1);
    -moz-box-shadow: inset 0px 0px 0px 2px rgba(0,0,0,1);
    box-shadow: inset 0px 0px 0px 2px rgba(0,0,0,1);
    font-weight: 700;
}
[data-product-attribute="set-rectangle"] .form-option {
    min-width: 36px;
    height: 36px;
    text-align: center;
    padding: 8px 7px 7px;
}
.form-option {
    border: 0 !important;
    -webkit-box-shadow: inset 0px 0px 0px 1px stencilColor('border-base-color');
    -moz-box-shadow: inset 0px 0px 0px 1px stencilColor('border-base-color');
    box-shadow: inset 0px 0px 0px 1px stencilColor('border-base-color');
    color: #616365;
    cursor: pointer;
    display: inline-block;
    font-size: 0; // 2
    font-weight: 700;
    margin-bottom: 9px;
    margin-left: 9px;
    transition: all 0.15s ease;

    &:hover {
        border-color: #000;
    }

}

.form-radio:checked + .form-option {
    -webkit-box-shadow: inset 0px 0px 0px 2px #38383e;
    -moz-box-shadow: inset 0px 0px 0px 2px #38383e;
    box-shadow: inset 0px 0px 0px 2px #38383e;
    background: #38383e;
    color: #ffffff;
    font-weight: 700;
}

.form-option-variant {
    display: inline-block;
    font-size: $body-fontSize; // 2

    pointer-events: none; // 1
    text-align: center;
}

.form-option-variant--color,
.form-option-variant--pattern {
    @include square(100%);
    min-width: initial;
    padding: 0;
}

.form-option-variant--pattern {
    background-position: center;
    background-repeat: no-repeat;
}


// Form input (small)
// -----------------------------------------------------------------------------
.form-input--small {
    font-size: $input-small-fontSize;
    height: $input-small-height;
    line-height: 1rem;
    padding: $input-small-padding;
}


// Form min/max input row
// -----------------------------------------------------------------------------
.form-minMaxRow {
    @include clearfix();
    margin-left: -$form-minMaxRow-column-gutter;
    margin-right: -$form-minMaxRow-column-gutter;

    .form-field {
        @include grid-column(4);
        margin-bottom: 0;
        padding-left: $form-minMaxRow-column-gutter;
        padding-right: $form-minMaxRow-column-gutter;
    }

    .button {
        margin-bottom: 0;
        padding-left: 0;
        padding-right: 0;
        width: 100%;
    }
}


// Form row modifications
// -----------------------------------------------------------------------------
//
// 1. If there are more than 2 fields per form-row--half, clear the left of every
// odd one.
//
// -----------------------------------------------------------------------------

.form-row--half > .form-field:nth-child(odd) {
    @include breakpoint("small") {
        clear: left;
    }
}

// Out of stock product attributes
[data-product-attribute] {
    .form-option {
        //overflow: hidden;
        position: relative;

        // scss-lint:disable NestingDepth
        &.unavailable {
            background-color: $productOptions-disable-background-color;
            opacity: $productOptions-disable-opacity;

            &:before {
                background-color: $productOptions-strikethrough-color;
                content: "";
                height: 2px;
                left: -7px;
                position: absolute;
                top: 17px;
                transform: rotate(-45deg);
                width: 141%;
            }
        }
    }
}

[data-product-attribute="set-radio"],
[data-product-attribute="product-list"] {
    .unavailable {
        opacity: $productOptions-disable-opacity * 2;
        text-decoration: line-through;
    }
}

components/stencil/heroCarousel/heroCaro:
@-webkit-keyframes fadeOutDown {
  from {
    opacity: 1;
  }

  to {
    opacity: 0;
    -webkit-transform: translate3d(0, 100%, 0);
    transform: translate3d(0, 100%, 0);
  }
}

@keyframes fadeOutDown {
  from {
    opacity: 1;
  }

  to {
    opacity: 0;
    -webkit-transform: translate3d(0, 100%, 0);
    transform: translate3d(0, 100%, 0);
  }
}

.fadeOutDown {
  -webkit-animation-name: fadeOutDown;
  animation-name: fadeOutDown;
}

@-webkit-keyframes fadeInUp {
  from {
    opacity: 0;
    -webkit-transform: translate3d(0, 100%, 0);
    transform: translate3d(0, 100%, 0);
  }

  to {
    opacity: 1;
    -webkit-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    -webkit-transform: translate3d(0, 100%, 0);
    transform: translate3d(0, 100%, 0);
  }

  to {
    opacity: 1;
    -webkit-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);
  }
}

.fadeInUp {
  -webkit-animation-name: fadeInUp;
  animation-name: fadeInUp;
}

.heroCarousel-wrapper {
    position: relative;
    .custom-arrow-left,
    .custom-arrow-right {
        width: calc(50% - 24px);
        height: 25px;
        z-index: 5;
        background-color: #fff;
        left: 0;
        bottom: -1px;
        position: absolute;
    }
}

.heroCarousel {
    margin-top: 0;
    height: auto;
    max-height: none;
    overflow: hidden;
    position: relative;
    &.slick-initialized {
        height: auto;
        max-height: none;
    }
    &.slick-slider,
    &.slick-slider.slick-dotted {
        margin-bottom: 0;
    }
    .slick-arrow-custom {
        fill: #fff;
        opacity: 0;
        width: 55px;
        height: 55px;
        visibility: hidden;
        border: none;
        padding: 9px;
        background-color: transparent;
        transition: all 0.3s ease;
        position: absolute;
        display: block;
        line-height: 0;
        font-size: 0;
        cursor: pointer;
        background: 0 0;
        color: transparent;
        top: 50%;
    }
    .slick-next {
        right: -50px;
    }
    .slick-prev {
        left: -50px;
    }
    .slick-dots {
        position: absolute;
        bottom: 40px;
        list-style: none;
        display: block;
        text-align: center;
        padding: 0;
        width: 100%;
        margin: 0;
        li {
            width: 13px;
            height: 13px;
            padding: 0;
            margin: 0 5px;
            background-color: transparent;
            position: relative;
            display: inline-block;
            cursor: pointer;
            button {
                width: 100%;
                height: 100%;
                opacity: 0.6;
                border: 2px solid #333;
                border-color: transparent;
                display: inline-block;
                margin: 0;
                padding: 0;
                position: relative;
                text-align: center;
                font-size: 0;
                border-color: #fff;
                border-radius: 50%;
            }
            &.slick-active button {
                opacity: 1;
                background-color: #fff;
            }
        }
    }
    &:hover {
        .slick-arrow-custom {
            opacity: 1;
            visibility: visible;
        }
        .slick-next {
            right: 50px;
        }
        .slick-prev {
            left: 50px;
        }
    }
    .slick-slide {
        .heroCarousel-title {
            visibility: hidden;
            animation-delay: 0ms;
            animation-name: fadeOutDown;
        }
        .heroCarousel-description {
            visibility: hidden;
            animation-delay: 0ms;
            animation-name: fadeOutDown;
        }
        .heroCarousel-action {
            visibility: hidden;
            animation-delay: 0ms;
            animation-name: fadeOutDown;
        }
    }
    .slick-active {
        .heroCarousel-title {
            visibility: visible;
            animation-delay: 100ms;
            animation-name: fadeInUp;
        }
        .heroCarousel-description {
            visibility: visible;
            animation-delay: 300ms;
            animation-name: fadeInUp;
        }
        .heroCarousel-action {
            visibility: visible;
            animation-delay: 500ms;
            animation-name: fadeInUp;
        }
    }
    .container{
        display: block;
        &:after,
        &:before{
            content:'';
            display: table;
        }
    }
}

.heroCarousel-slide {
    width: 100%;
    height: 100%;
    position: relative;
    overflow: hidden;
    vertical-align: top;
}

.heroCarousel-image {
    z-index: 1;
    width: 100%;
    position: relative;
}

.heroCarousel-content {
    z-index: 5;
    width: 100%;
    left: 0;
    right: 0;
    top: 50%;
    position: absolute;
    transform: translate3d(0,-50%, 0);
    background-color: transparent;
    padding-left: 50px;
    padding-right: 50px;
    text-align: left;
    display: block;
    .heroCarousel-content-wrapper{
        max-width: 500px;
    }
    .heroCarousel-title {
        margin-top: 0;
        font-size: 30px;
        font-weight: 700;
        line-height: 1.5;
        letter-spacing: 0.25px;
        text-transform: capitalize;
        color: stencilColor('color-heroCarousel-title');
        font-family: 'Archivo', sans-serif;
    }
    .heroCarousel-description {
        color: stencilColor('color-heroCarousel-content');
        font-size: 16px;
        letter-spacing: 0.25px;
        font-family: "Roboto", Arial, Helvetica, sans-serif;
    }
    .heroCarousel-action {
        display: inline-block;
        text-align: center;
        vertical-align: middle;
        padding: 10px 25px;
        border: 0px solid #bb2d02;
        border-radius: 5px;
        background: #ff4903;
        background: linear-gradient(to bottom, #ff4903, #ea3802);
        box-shadow: #aa2500 0px 3px 0px 0px;
        font: normal normal bold 13px roboto;
        color: #ffffff;
        text-decoration: none;
        &:hover{
            border: 0px solid #ea3802;
            background: #ff5804;
            background: linear-gradient(to bottom, #ff5804, #ff4302);
            color: #ffffff;
            text-decoration: none;
        }
        &:active{
            background: #bb2d02;
            background: linear-gradient(to bottom, #bb2d02, #ea3802);
        }
    }
}

.heroCarousel-video-wrapper{
    position: relative;
    overflow: hidden;
    .heroCarousel-video{
        position: absolute;
        top: 50%;
        left: 0;
        width: 100%;
        height: 0;
        padding-bottom: stencilString('carousel-video-ratio');
        transform: translateY(-50%);
        transition: opacity .3s ease;
        z-index: 5;
        iframe{
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
        }
        @media (max-width: 1024px){
            display: none;
        }
    }
    ~ .heroCarousel-content{
        .heroCarousel-title {
            color: stencilColor('color-heroCarousel-video-title');
        }
        .heroCarousel-description {
            color: stencilColor('color-heroCarousel-video-content');
        }
    }
}
@media (max-width: 1450px) {
    .heroCarousel-slide {
        .heroCarousel-title {
            font-size: 30px;
            line-height: 1.4;
        }
    }
}

@media (max-width: 1024px) {
    .heroCarousel-slide {
        .heroCarousel-title {
            font-size: 40px;
            margin-bottom: 30px;
        }
    }
    .heroCarousel {
        .slick-prev {
            opacity: 1;
            visibility: visible;
            left: 40px;
        }
        .slick-next {
            opacity: 1;
            visibility: visible;
            right: 40px;
        }
    }
    .heroCarousel-content{
        text-align: center;
        .heroCarousel-content-wrapper{
            margin: 0 auto;
        }
    }
    .heroCarousel-image{
        min-height: 550px;
        object-fit: cover;
    }
}
@media (max-width: 992px) {
    .heroCarousel-wrapper {
        .custom-arrow-left,
        .custom-arrow-right{
            display: none;
        }
    }
    .heroCarousel{
        margin-bottom: 0!important;
       .slick-arrow-custom{
            display: none!important; 
       }
        .slick-dots{
            bottom: unset;
            top: 43%;
            transform: translateY(-43%);
        }
    }
    .heroCarousel-slide {
        max-height: 768px;
        min-height: 500px;
        height: 100vh;
        background: #fff;
        .heroCarousel-title {
            font-size: 32px;
            line-height: 1.3;
            margin-bottom: 15px;
        }
    }
     .heroCarousel-content {
        top: auto;
        left: auto;
        position: relative;
        transform: none;
        padding: 50px 30px!important;
    }
    .heroCarousel-image{
        min-height: unset;
    }
    .heroCarousel-content{
        .heroCarousel-title {
            color: stencilColor('color-heroCarousel-mobile-title')!important;
        }
        .heroCarousel-description {
            color: stencilColor('color-heroCarousel-mobile-content')!important;
        }
    }
}

@media (max-width: 767px) {
    .heroCarousel-slide {
        height: auto;
        max-height: none;
    }
    .heroCarousel {
        .slick-dots {
            top: 280px!important;
        }
    }
    .heroCarousel-image{
        min-height: 300px;
        object-fit: cover;
    }
}

@media (max-width: 480px) {
    .heroCarousel, 
    .heroCarousel:hover {
        .slick-next {
            right: 0;
        }
        .slick-prev {
            left: 0;
        }
    }
}

@media (max-width: 414px) {
    .heroCarousel-slide {
        .heroCarousel-title {
            font-size: 28px;
            letter-spacing: 4px;
        }
        .heroCarousel-description {
            font-size: 12px;
            line-height: 20px;
        }
        .heroCarousel-content {
            padding: 40px 15px!important;
        }
    }
}

components/stencil/banners/banners.scss:
// =============================================================================
// Banners Component
// =============================================================================


// Banners
// -----------------------------------------------------------------------------
//
// Purpose: Control the look and feel of the top and bottom site banners
//
// -----------------------------------------------------------------------------

.banners {
    color: stencilColor("banner-text-color");
    text-align: center;
    p:last-child {
        margin-bottom: 0;
    }
    a {
        color: stencilColor("banner-link-color");
        &:hover {
            color: stencilColor("banner-link-color-hover");
        }
    }
    &[data-banner-location="bottom"] {
        clear: both;
    }
    & .Home-Banner-Top {
        & .row {
            margin-left: -1px;
            margin-right: -1px;
            & .col-sm-6 {
                padding: 1px;
                &:hover {
                    & a {
                        img {
                            opacity: 0.6;
                            filter: alpha(opacity=60); 
                        }
                    }
                }
                & a {
                    & img {
                        width: 100%;
                        max-width: 100%;
                        
                        opacity: 1;
                        filter: alpha(opacity=100);
                    }
                }
                & h4 {
                    margin: 0;
                    position: absolute;
                    top: 0%;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    & a {
                        font-weight: 700;
                        display: block;
                        width: 100%;
                        height: 100%;
                        display: -ms-flexbox;
                        display: -webkit-flex;
                        display: flex;
                        -ms-flex-align: center;
                        -webkit-align-items: center;
                        align-items: center;
                        -ms-flex-pack: center;
                        -webkit-justify-content: center;
                        justify-content: center;
                        text-align: center;
                        padding: 0 20px;
                        color: stencilColor("banner-link-color");
                        &:hover {
                            color: stencilColor("banner-link-color-hover");
                        }
                    }
                }
            }
        }
    }
    & .banner-category-bottom {
        border-top: 1px solid stencilColor('border-base-color');
        margin-top: 5px;
        text-align: left;
        & .desc-top {
            padding-top: 32px;
            padding-bottom: 45px;
            border-bottom: 1px solid stencilColor('border-base-color');
            margin-bottom: 40px;
            width: 100%;
            float: left;
            & .desc-top-left {
                width: 255px;
                float: left;
                @media (max-width: 580px) {
                    width: 170px;
                    padding-right: 20px;
                }
                @media (max-width: 480px) {
                    width: 100%;
                    text-align: center;
                    margin-bottom: 20px;
                }
                & img {
                    border-radius: 50%;
                    -webkit-border-radius: 50%;
                    -moz-border-radius: 50%;
                    -ms-border-radius: 50%;
                    -o-border-radius: 50%;

                }
            }
            & .desc-top-right {
                width: calc(100% - 255px);
                float: left;
                @media (max-width: 580px) {
                    width: calc(100% - 170px);
                }
                @media (max-width: 580px) {
                    width: 100%;
                }
                & h3 {
                    font-size: 22px;
                    font-weight: 700;
                    letter-spacing: .015em;
                    text-align: left;
                    @media (max-width: 580px) {
                        margin-top: 0;
                    }
                }
                & p {
                    font-size: 16px;
                    letter-spacing: .035em;
                }
            }
        }
        & .desc-bottom {
            clear: both;
            margin-bottom: 45px;
            @media (max-width: 991px) {
                margin-bottom: 78px;
            }
            & ul {
                margin-top: 30px;
                margin-bottom: 28px;
                display: -webkit-box;
                /* OLD - iOS 6-, Safari 3.1-6 */
                display: -moz-box;
                /* OLD - Firefox 19- (buggy but mostly works) */
                display: -ms-flexbox;
                /* TWEENER - IE 10 */
                display: -webkit-flex;
                /* NEW - Chrome */
                display: flex;
                /* NEW, Spec - Opera 12.1, Firefox 20+ */
                flex-wrap: wrap;
                flex-wrap: wrap;
                @media (max-width: 991px) {
                    margin-bottom: 0;
                }
                & li {
                    border-right: 1px solid stencilColor('border-base-color');
                    @media (min-width: 992px) {
                        width: 24.95%;
                    }
                    @media (max-width: 991px) {
                        border-right: none;
                        width: 100%;
                        margin-bottom: 22px;
                    }
                    &:last-child {
                        border-right: none;
                    }
                    & h5 {
                        margin-top: 5px;
                        margin-bottom: 5px;
                        letter-spacing: .015em;
                        text-transform: capitalize;
                        font-weight: 700;
                        @media (max-width: 991px) {
                            font-size: 12px;
                        }
                    }
                    & p {
                        color: stencilColor('color-textBase');
                        &:last-child {
                            margin-bottom: 0;
                        }
                    }
                }
            }
        }
        & p {
            color: stencilColor('color-black');
            line-height: 21px;
            & a {
                color: stencilColor('color-textLinkSecondary');
                &:hover {
                    color: stencilColor('color-textLinkSecondary--hover');                }
            }
        }
    }

}

.banner {

    + .banner {
        margin-top: spacing("single");
    }
}
.categoryBanner{
    margin-top: 30px;
}
/*Home Top Banners*/

.banner-item {
    overflow: hidden;
    margin-bottom: 30px;
    @media (max-width: 991px){
        overflow: inherit;
    }
}
.banner-item {
    & a {
        position: relative;
        display: block;
        text-align: center;
        border-radius: 4px;
        -moz-border-radius: 4px;
        -webkit-border-radius: 4px;
        img{
            border-radius: 4px;
            -moz-border-radius: 4px;
