//View Mode Buttons (GRID/LIST)
.view-mode-btn {
    display: -webkit-box;
    /* OLD - iOS 6-, Safari 3.1-6 */
    display: -moz-box;
    /* OLD - Firefox 19- (buggy but mostly works) */
    display: -ms-flexbox;
    /* TWEENER - IE 10 */
    display: -webkit-flex;
    /* NEW - Chrome */
    display: flex;
    /* NEW, Spec - Opera 12.1, Firefox 20+ */
    justify-content: space-between;
    align-items: center;
    border-radius: 3px;
    background-color: #fff;
    border: 1px solid #d7d7d7;
    float: left;
    @media (max-width: 1200px) {
        margin-top: 10px;
        display: inline-flex;

    }
    @media (max-width: 600px) {
        margin-bottom: 10px;
        float: left;
    }
    @media (max-width: 550px) {
        float: right;
        margin-right: 18px;
    }
}

.view-mode-btn a,
.view-mode-btn a:hover,
.view-mode-btn a:active,
.view-mode-btn a:focus {
    font-size: 11px;
    font-weight: 700;
    position: relative;
    display: -webkit-box;
    /* OLD - iOS 6-, Safari 3.1-6 */
    display: -moz-box;
    /* OLD - Firefox 19- (buggy but mostly works) */
    display: -ms-flexbox;
    /* TWEENER - IE 10 */
    display: -webkit-flex;
    /* NEW - Chrome */
    display: flex;
    /* NEW, Spec - Opera 12.1, Firefox 20+ */
    align-items: center;
    justify-content: center;
    height: 36px;
    margin-right: 0;
    margin-left: 0;
    color: rgba(0, 0, 0, .41);
    text-decoration: none;
}

.view-mode-btn a>div {
    width: 30px;
    margin-left: 4px;
    margin-right: 2px;
}

.view-mode-btn a>span {
    text-transform: uppercase;
    font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
    font-size: 12px;
    font-weight: 700;
    margin-top: 2px;
    padding-right: 12px;
}

.view-mode-btn a.current-view {
    cursor: default;
    color: rgba(0, 0, 0, 1.0);
}

.view-mode-btn a .icon-bar {
    position: relative;
    top: -1px;
    width: 14px;
    height: 4px;
    margin: 1px auto 0 auto;
    transition: all .15s ease;
    opacity: .41;
    background: #232323;
    filter: alpha(opacity=41);
}

.view-mode-btn a#grid-view {
    border-right: 1px solid #d7d7d7;
}

.view-mode-btn a#grid-view .icon-bar {
    width: 4px;
}

.view-mode-btn a#grid-view .icon-bar:before,
.view-mode-btn a#grid-view .icon-bar:after {
    position: absolute;
    display: block;
    width: 4px;
    height: 4px;
    content: '';
    transition: all .15s ease;
    background: #232323;
}

.view-mode-btn a#grid-view .icon-bar:before {
    left: -5px;
}

.view-mode-btn a#grid-view .icon-bar:after {
    right: -5px;
}

.view-mode-btn a.current-view .icon-bar {
    opacity: 1;
    filter: alpha(opacity=100);
}

