    & .owl-nav>div {
        opacity: 0;
        @media (max-width: 991px) {
            display: none;
        }
        &.owl-prev {
            left: -21px;
        }
        &.owl-next {
            right: -21px;
        }
    }

    & .owl-dots {
        position: absolute;
        bottom: 10px;
        left: 50%;
        -moz-transform: translateX(-50%);
        -webkit-transform: translateX(-50%);
        -ms-transform: translateX(-50%);
        -o-transform: translateX(-50%);
        transform: translateX(-50%);
        @media (min-width: 992px) {
            display: none;
        }
        @media (max-width: 1199px) {
            bottom: -20px;
            width: 200px;
            overflow: hidden;
            height: 15px;
            text-align: center;
        }
    }
    & .owl-dots>div {
        position: relative;
        display: inline-block;
        margin: 0 6px !important;
        width: 12px;
        height: 12px;
        /* Dots Puff Style  */
        & span {
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            outline: none;
            border-radius: 50%;
            background-color: rgba(0, 0, 0, 0.4);
            text-indent: -999em;
            /* make the text accessible to screen readers */
            cursor: pointer;
            position: absolute;
        }
        & span:focus {
            outline: none;
        }
        & span {
            border: 1px solid rgba(0, 0, 0, 0.4);
            -webkit-transition: border-color 0.3s ease;
            transition: border-color 0.3s ease;
        }
        & span::after {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            visibility: hidden;
            background: rgba(0, 0, 0, 0.4);
            border-radius: 50%;
            box-shadow: 0 0 1px rgba(0, 0, 0, 0.4);
            opacity: 0;
        }
        & span:hover,
        & span:focus,
        &.active span {
            border-color: #2974F0;
        }
        &.active span::after {
            background: #fff;
            visibility: visible;
            opacity: 1;
        }
    }
}

