            &.has-subMenu{
                &::after {
                    position: absolute;
                    top: 3px;
                    right: -15px;
                    content: '\f107';
                    font-family: FontAwesome;
                    color: stencilColor('mainNav-topLevel-color');
                    font-size: 12px;
                    font-weight: 400;
                }
