                    border-top: 1px solid stencilColor('mainNav-dropdown-border-color');
                }
                & li > a {
                    position: relative;
                    display: block;
                    padding: 7px 15px 7px;
                    text-transform: capitalize;
                    color: stencilColor('mainNav-dropdown-text-color');
                }
                & li:hover > a {
                    color: stencilColor('mainNav-dropdown-text-color-hover');
                }
            }
            & li > .navPage-childList {
                position: absolute;
                z-index: 1;
                top: 0;
                left: 100%;
                display: none;
                width: 100%;
                min-width: 220px;
                margin-left: -10px;
                background: stencilColor('mainNav-dropdown-backgroundColor');
                -webkit-box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
                -moz-box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
                box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
                border: 1px solid #d0d0d0;
                border-radius: 3px;
                &::before {
                    position: absolute;
                    width: 7px;
                    height: 100%;
                    margin-left: -7px;
                    content: '';
                    background-color: transparent;
                }
                & li:hover {
                    &:before {
                        position: absolute;
                        color: stencilColor('megamenu-sub-children-colorHover');
                        left: 0;
                        top: 9px;
                        content: "\f0da";
                        font: normal normal normal 12px/1 FontAwesome;
                        transition: all .3s ease 0s;
                        opacity: 1;
                        filter: alpha(opacity=0);
                    }
                    > a {
                        margin-left: 15px;
                    }
                }
            }
            & li:hover > .navPage-childList {
                display: block;
                -webkit-animation: fadeIn .3s;
                -o-animation: fadeIn .3s;
                animation: fadeIn .3s;
            }
            & li > .navPage-subMenu {
                position: absolute;
                z-index: 1;
                top: 0;
                left: 100%;
                display: none;
                width: 100%;
                min-width: 220px;
                margin-left: 6px;
                background: stencilColor('mainNav-dropdown-backgroundColor');
                -webkit-box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
                -moz-box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
                box-shadow: 0 1px 10px rgba(0, 0, 0, .1);
                border: 1px solid #d0d0d0;
                &::before {
                    position: absolute;
                    width: 7px;
                    height: 100%;
                    margin-left: -7px;
                    content: '';
                    background-color: transparent;
                }
            }
            & li:hover > .navPage-subMenu {
                display: block;
                -webkit-animation: fadeIn .3s;
                -o-animation: fadeIn .3s;
                animation: fadeIn .3s;
            }
        }
        &:hover > a > span.triangle-with-shadow {
            display: block;
            -webkit-animation: fadeIn .3s;
            -o-animation: fadeIn .3s;
            animation: fadeIn .3s;
        }
        &:hover > .navPage-subMenu {
            display: block;
            -webkit-animation: fadeIn .3s;
            -o-animation: fadeIn .3s;
            animation: fadeIn .3s;
        }
        &:not(.hasMegamenu) {
            & > .navPage-subMenu {
                ul {
                    & li:hover {
                        &:before {
                            position: absolute;
                            color: #878787;
                            left: 15px;
                            top: 11px;
                            content: "\f0da";
                            font: normal normal normal 12px/1 FontAwesome;
                            transition: all .3s ease 0s;
                            opacity: 1;
                            filter: alpha(opacity=0);
                        }
                        > a {
                            margin-left: 15px;
                            color: stencilColor('mainNav-dropdown-text-color-hover');
                        }
                    }
                }
            }
        }
    }


