// =============================================================================
// CART (CSS)
// =============================================================================
//
// 1. Override server-generated inline-css
//
// -----------------------------------------------------------------------------
// Cart variables
// -----------------------------------------------------------------------------
$cart-item-spacing: spacing('single');
$cart-thumbnail-maxWidth: remCalc(100px);
$cart-thumbnail-height: remCalc(100px);
$cart-item-label-offset: $cart-thumbnail-maxWidth + $cart-item-spacing;
// Cart layout
// -----------------------------------------------------------------------------
//
// 1. On mobile and tablet we need to re-structure the table to appear more like
//    a list, then visually show it as a table on larger screens only.
//
// 2. Hide the table headings on mobile and tablet, show them for larger screens.
//
// -----------------------------------------------------------------------------
.page-type-cart {
    .page-content {
        position: relative;
        padding-top: 20px;
    }
}
.cart {
    display: block; // 1
    width: 100%;
    @include breakpoint('medium') {
        display: table; // 1
    }
}

.cart-header {
    background: #ffffff;
    -moz-box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.08);
    -webkit-box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.08);
    box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.08);
    border-bottom: 1px solid #e5e5e5;
    display: none; // 2
    @include breakpoint('medium') {
        display: table-header-group; // 2
        text-align: left;
    }
}

.cart-header-item {
    font-family: $fontFamily-headings;
    font-size: 12px;
    font-weight: 700;
    min-height: 41px;
    padding: 16px 30px 12px;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
    text-transform: uppercase;
    color: #000;
    &:last-child {
        text-align: center;
    }
}

.cart-list {
    display: block; // 1
    border-top: container('border');
    @include breakpoint('medium') {
        display: table-row-group; // 1
    }
}

.cart-item {
    display: block; // 1
    clear: both;
    padding: $cart-item-spacing 0;
    border-bottom: container('border');
    @include breakpoint('small') {
        padding-left: 0;
        @include clearfix;
    }
    @include breakpoint('medium') {
        display: table-row; // 1
        padding-left: 0;
        &:before,
        &:after {
            display: none;
        }
    }
}

.cart-item-block {
    display: block; // 1
    @include breakpoint('medium') {
        display: table-cell; // 1
        padding: 35px 0;
        vertical-align: top;
    }
    .definitionList {
        margin-bottom: spacing('quarter');
        color: #8c8c8c;
    }
}

.cart-item-figure {
    float: left;
    width: grid-calc(4, $total-columns);
    height: $cart-thumbnail-height;
    margin-bottom: $cart-item-spacing;
    padding-right: 0;
    padding-left: 18px;
    text-align: right;
    @include breakpoint('small') {
        // height: auto;
        width: grid-calc(4, $total-columns);
    }
    @include breakpoint('medium') {
        float: none;
        width: grid-calc(1.5, $total-columns);
        text-align: left;
    }
}

.cart-item-fixed-image {
    width: 100%;
}

.cart-item-image {
    width: auto;
}

.cart-item-title {
    display: table-cell;
    padding-left: 42px;
    @include breakpoint('small') {
        display: block;
        overflow: hidden;
        height: auto;
        margin-bottom: $cart-item-spacing;
    }
    @include breakpoint('medium') {
        display: table-cell;
    }
    a[data-item-edit] {
        font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
        font-size: 12px;
        color: $anchor-font-color;
        font-weight: bold;
        &:hover {
            color: $anchor-font-color-hover;
        }
    }
}
.btn-continue {
    position: absolute;
    right: 0;
    top: 13px;
    @media (max-width: 480px) {
        position: relative;
        right: auto;
        top: auto;
        margin-bottom: 20px;
        width: 100%;
    }
}
.bgr-cart{
    width: 100%;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.08);
    background-color: #ffffff;
    border-radius: 3px;
    overflow: hidden;
    // padding-bottom: 50px;
    .alert{
        margin: 20px;

    }
}
.warp-border {
    border-radius: 3px;
    margin-bottom: 30px;
}

.cart-item-info {
    clear: both;
    width: 100%;
    @include breakpoint('small') {
        float: left;
        clear: none;
        overflow: hidden;
        width: grid-calc(4, $total-columns);
        + .cart-item-info {
            margin-top: 0;
        }
    }
    @include breakpoint('medium') {
        float: none;
        width: grid-calc(2, $total-columns);
        &:last-child {
            text-align: center;
        }
    }
    + .cart-item-info {
        margin-top: spacing('half');
    }
    &:not(.cart-item-quantity) {
        font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
        font-size: 13px;
        padding-top: 45px;
        & strong {
            font-weight: 600;
        }
        &:last-of-type {
            & strong {
                font-weight: 600;
                @media (min-width: 767px) {
                    padding-right: 10px;
                }
            }
        }
    }
    &.cart-item-quantity {
        padding-top: 38px;
    }
}

@media (min-width: 992px) {
    .cart-item-info.cart-item-quantity {
        width: 15.5%;
    }
}

@media (max-width: 991px) {
    .cart-item-info.cart-item-quantity {
        width: 25%;
    }
}

@media (max-width: 767px) {
    .cart-item-figure {
        height: auto;
    }
    .cart-item-title {
        padding-left: 30px;
        padding-right: 15px;
    }
    .cart-item-info {
        display: block;
        width: 100% !important;
        padding-top: 5px !important;
        text-align: left;
        padding-left: 15px;
        padding-right: 15px;
        & .cart-item-label {
            font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
            font-size: 12px;
            font-weight: 600;
            float: left;
            width: 33.33%;
            text-align: left;
            float: left;
        }
        .cart-item-value  {
            float: left;
            padding-left: 24px;
        }
        &.cart-item-quantity {
            margin-top: 5px;
            display: inline-block;
            & .cart-item-label {
                padding-top: 7px;
                margin-bottom: 20px;
            }
            & .form-increment {
                display: inline-block;
                float: left;
                margin-left: 0 !important;
                padding-left: 24px;
            }
        }
    }
}

// Cart content
// -----------------------------------------------------------------------------
.cart-header-quantity,
.cart-item-quantity,
.cart-header-price,
.cart-item-price {
    @include breakpoint('medium') {
        text-align: center;
    }
}

.cart-item-quantity {
    .form-increment {
        display: inline-block;
        margin: 0 spacing('eighth') 1px spacing('single');
        vertical-align: middle;
        @include breakpoint('medium') {
            margin-left: 0;
        }
        button[data-action='dec'] {
            margin-right: -4px;
        }
        button[data-action='inc'] {
            margin-left: -4px;
        }
    }
}

.cart-item-label {
    display: block;
    float: left;
    width: grid-calc(4, $total-columns);
    width: 100%;
    margin-bottom: spacing('quarter');
    text-align: right;
    text-align: left;
    @include breakpoint('medium') {
        display: none;
    }
}

.cart-item-value {
    padding-left: $cart-item-spacing;
    @include breakpoint('medium') {
        padding-left: 0;
    }
    &.price--discounted {
        margin-right: 5px;
    }
}

.cart-item-brand {
    margin-bottom: 10px;
    margin-top: 10px;
    color: #8c8c8c;
    letter-spacing: 0;
    font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
}

.cart-item-name {
    font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
    font-size: 13px;
    font-weight: stencilFontWeight('card-title-font');
    margin: 0 0 10px;
    letter-spacing: 0;
    & a {
        line-height: 1.5em;
        color: $anchor-font-color-hover;
        letter-spacing: 0;
        font-weight: 500;
        &:hover {
            color: $anchor-font-color;
        }
    }
    + .definitionList {
        margin-top: spacing('quarter');
    }
}

.cart-item-options--giftWrapping {
    margin-top: 20px;
    letter-spacing: 0;
    font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
    dl,
    dt {
        font-weight: 600;
        display: inline-block;
    }
    .cart-item-option-description {
        color: $body-font-color;
        display: inline-block;
        margin-right: 10px;
        & a {
            font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
            font-weight: 600;
            background: #969696;
            color: #ffffff;
            text-transform: uppercase;
            font-size: 11px;
            line-height: 1.3;
            padding: 4px 8px 3px;
            border-radius: 3px;
            margin-left: 5px;
            margin-right: 5px;
            margin-bottom: 5px;
            display: inline-block;
            &:hover {
                background: #000000;
            }
        }
        & .cart-item-option-actions {
            margin-left: 10px;
        }
    }
}

.cart-remove-button {
    display: inline-block;
    clear: both;
    padding: 0px 0;
    @media (max-width: 767px){
        margin-right: 0;
        padding-left: 30px;
    }
}

.cart-remove {
    width: auto;
    height: auto;
    padding: spacing('eighth');
    padding: 0;
    cursor: pointer;
    transition: background-color .15s ease;
    text-align: center;
    color: #f14d48;
    &.cart-remove--spacer {
        visibility: hidden;
    }
    & svg {
        position: relative;
        top: -2px;
        left: 0px;
        width: 16px;
        height: 16px;
        vertical-align: middle;
        fill: #f14d48;
    }
    & span {
        font-family: stencilFontFamily('body-font'), Arial, Helvetica, sans-serif;
        font-weight: 400;
        margin-left: 5px;
        letter-spacing: 0;
        color: #e84625;
    }
    &:hover {
        & span {
            border-bottom: 1px solid #f14d48;
        }
    }
}

// Cart totals
// -----------------------------------------------------------------------------
.cart-totals {
    width: 100%;
    text-align: right;
    margin-bottom: 0;
    list-style: none;
    @media (min-width: 767px) {
        margin-left: 0;
    }

}

.cart-total {
    border-bottom: 0;
    margin-right: 30px;
    @include clearfix;
    + .cart-total {
        border-top: container('border');
    }
    &.gift_certificates {
        display: none;
    }
    .form-input {
        display: inline-block;
        width: grid-calc(9, $total-columns);
        padding: spacing('third');
        vertical-align: top;
