        margin-bottom: spacing('half');
        padding: spacing('third') spacing('half');
    }
    .form-inlineMessage {
        text-align: left;
    }
    &:first-child .cart-total-value {
        font-size: remCalc(16px);
        font-weight: 700;
        letter-spacing: 0;
    }
    &.coupons {
        margin-right: 15px;
        margin-left: 15px;
        padding: 0 0 25px 0;
        border-bottom: 1px solid #e8e8e8;
        @media (min-width: 768px) {
            max-width: 455px;
            padding: 0 0 0 30px;
            margin-right: 10px;
            border-bottom: none;
            margin-left: 0;
        }
        h3 {
            font-size: 16px;
            font-weight: 700;
        }
        .cart-form.coupon-code {
            display: block !important;
            #couponcode {
               @media (max-width: 991px) {
                    width: 73%;
                }
            }
            .btn {
                @media (max-width: 991px) {
                    width: 25%;
                }
            }
        }
        .coupon-code-add {
            display: none;
        }
        >p {
            margin-bottom: 0;
            clear: both;
            &.support-text {
                margin-top: 15px;
                display: inline-block;
                font-style: italic;
                font-weight: 700;
                a {
                    text-decoration: underline;
                }
            }
        }
    }
}

.cart-total-label {
    display: block;
    width: grid-calc(4, $total-columns);
    width: grid-calc(6, $total-columns);
    padding-left: 0;
    text-align: left;
    width: 70%;
    font-size: 13px;
    line-height: 20px;
    letter-spacing: 0;
    @media (max-width: 991px) {
        width: 50%;
    }

}

.cart-total-value {
    width: grid-calc(6, $total-columns);
