        ul {
            li.estimator-form-row {
                list-style: none;
                dl {
                    display: inline-block;
                    width: 100%;
                    margin-bottom: 5px;
                    .estimator-form-label {
                        text-align: left;
                        position: relative;
                        margin-top: -18px;
                        input {
                            margin-top: 4px;
                            margin-left: 3px;
                            position: relative;
                            z-index: 10;
                            margin-bottom: -17px;
                            display: inline-block;
                            vertical-align: bottom;
                            width: 16px;
                            height: 16px;
                            opacity: 0;
                        }
                    }
                }
            }
        }
    }
}

.cart-total-label,
.cart-total-value {
    font-family: stencilFontFamily('card-title-font'), Arial, Helvetica, sans-serif;
    float: left;
    padding: 17px 0 17px;
    color: #000;
}

.cart-total-grandTotal {
