}

.cart-additionalCheckoutButtons {
    clear: both;
    float: right;
    text-align: center;
    margin-bottom: 50px;
    margin-top: 10px;
    @media (max-width: 600px) {
        width: 100%;
    }
}

.cart-actions {
    clear: both;
    @include clearfix;
    float: right;
    text-align: center;

    @media (max-width: 991px) {

    }
    @media (min-width: 767px) {
        margin-right: 30px;
        padding-left: 43px;
        padding-right: 0;
    }
    .btn-lg {
        font-size: 16px;
        width: 100%;
        max-width: 100%;
        @media (max-width: 480px) {
