/* Halo Fly To Cart Notification */
.jGrowl {
    z-index: 9999;
    font-size: 12px;
    position: fixed;
}

.jGrowl.top-right {
    right: 0px;
    top: 0px;
}

.jGrowl-notification .jGrowl-close {
    background-color: transparent;
    color: #000000;
    border: none;
    z-index: 99;
    float: right;
    font-weight: bold;
    font-size: 1em;
    cursor: pointer;
}

.jGrowl-notification {
    position: relative;
    opacity: 1;
    box-shadow: 0 0px 15px rgba(0, 0, 0, 0.1);
    background: #fff;
    margin: 20px;
    border: 0;
    width: 340px;
    border-radius: 0;
    padding: 0;
    @media (max-width: 360px) {
        width: 320px;
    }
    @media (max-width: 320px) {
        width: 280px;
    }
    & .jGrowl-close {
        background: stencilColor('button--modal-close-bgColor');
        color: stencilColor('button--modal-close-color');
        position: absolute;
        top: -10px;
        right: -10px;
        width: 26px;
        height: 26px;
        font-size: 23px;
        font-weight: 400;
        line-height: 0;
        padding: 0;
        display: flex;
        align-items: center;
        justify-content: center;
        &:hover {
            background: stencilColor('button--modal-close-bgColorHover');
        }
    }
    & .jGrowl-message {
        border: 1px solid #ebebeb;
    }
    & .previewCartItem-content h6 {
        margin: 0;
        font-size: 12px;
        text-transform: none;
        line-height: 1.4em;
        color: #178dc9;
        padding-top: 2px;
        padding-bottom: 4px;
    }
    & .previewCartItem-content p {
        margin: 0;
        & a {
            font-weight: 500;
        }
    }
}

.jGrowl-closer {
    margin: 0 20px;
    background: #000;
    text-align: center;
