    @media (max-width: 767px){
      width: 33.333333%;
      padding: 0 10px!important;
      margin-bottom: 10px;
    }
    img{
      width: 90px;
      height: 90px;
      object-fit: scale-down;
    }
    span{
      margin-top: 22px;
      font-size: 13.66px;
      display: block;
      font-weight: 700;
      font-family: Roboto,Arial,Helvetica,sans-serif;
      line-height: 22px;
      letter-spacing: 0.015em;
      text-align: center;
      padding: 0 5px;
    }
  }
}
#quick-menu{
  .navPages-label{
    line-height: 1;
    font-size: 10px;
    font-weight: 500;
    padding: 3px 5px 2px;
    letter-spacing: 0;
    border-radius: 1px;
    border: 1px solid;
    display: inline-block;
    left: 100%;
    top: -20px;
    margin-left: 0;
    position: absolute;
    -webkit-transform: translateX(-50%);
    transform: translateX(-50%);
    &:before{
      content: "";
      position: absolute;
      border: 5px solid transparent;
      left: 50%;
      right: auto;
      top: 100%;
      transform: translateX(-50%);
    }
    &.new-label{
      color: #fff;
      border-color: #ea3a02;
      background-color: #ea3a02;
      &:before{
        border-top-color: #ea3a02;
        border-right-color: transparent;
      }
    }
    &.sale-label{
      color: #fff;
      border-color: #ff4903;
      background-color: #ff4903;
      &:before{
        border-top-color: #ff4903;
        border-right-color: transparent;
      }
    }
    &.hot-label{
      color: #fff;
      border-color: #ffbb49;
      background-color: #ffbb49;
      &:before{
        border-top-color: #ffbb49;
        border-right-color: transparent;
      }
    }
  }
}

span.numbercircle {
  background: #00b9e4;
  border-radius: 0.8em;
  -moz-border-radius: 0.8em;
  -webkit-border-radius: 0.8em;
  color: #ffffff;
  display: inline-block;
  font-weight: bold;
  line-height: 1.6em;
  margin-bottom: 5px;
  text-align: center;
  width: 1.6em;
}
.productView-images .sale-badge, .productView-images .new-badge{
  // position:static;
  top:unset;
  right:unset;
}

.badges-container{
  position:absolute;
  right:20px;
  .product-out-of-stock, .new-badge, .sale-badge{
    display:block;
    margin-bottom:10px;
  }
}
.new-badge, .sale-badge{
  position:relative;
  top:unset;
  right:unset;
}
.prod-image{
  .new-badge{
    display:none;
  }
}
.prod-image{
  .badges-container{
    .new-badge{
      display:block;
    }
  }
}

.new-badge ~ .sale-badge{
  right:unset;
}
.product-out-of-stock{
  // position: absolute;
  // top: 23px;
  // right: 15px;
  .sale-stock-badge{
    width: 90px;
    background: #38383e;
    height: 18px;
    text-align: left;
    font-weight: 600;
    font-size: 10px;
    letter-spacing: .015em;
    line-height: 18px;
    font-family: "Roboto", Arial, Helvetica, sans-serif;
    padding: 0 5px 0 5px;
    text-transform: uppercase;
    color: white;
    z-index:1;
    position:relative;
    &:before{
      content: "";
      width: 1px;
      height: 1px;
      position: absolute;
      top: 0px;
      right: -19px;
      border: 18px solid transparent;
      border-top-color: #38383e;
    }
  }
}
