//CART CHANGES BAPO
.btn-continue{
  display:none;
}
.cart-header-item{
  &.cart-header-price{
  display:none;
  }
}
.cart-item-block{
  &.cart-item-price{
  display:none;
  }
}
.shipping-estimator{
  .shipping-estimate-submit{
    background:#777a7c;
    border: 2px solid #777a7c;
    &:hover{
      background:#000000;
      border: 2px solid #000000;
    }
  }
  .estimator-form{
    .estimator-form-input{
      @media(max-width:1199px){
        width:100%;
      }
    }
  }
}
.bgr-cart{
  padding-bottom:22px;
  .cart-header-row{
    background: #ffffff;
    box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.08);
    border-bottom: 1px solid #e5e5e5;
    margin-bottom:22px;
    .cart-header-item{
      display:inline-block;
      margin-bottom:0;
      padding:16px 44px 12px;
      &:first-child{
        padding-right:40px;
        width:40%;
        padding: 16px 30px 12px;
        @media(max-width:1100px){
          width: 43.5%;
        }
        @media(max-width:1024px){
          width:42.5%;
        }
        @media(max-width:991px){
          width:33.5%;
        }
      }
    }
    // @media(max-width:991px){
    //   display:none;
    // }
  }
  .cart{
    width:66%;
    float:left;
    // .cart-header{
    //   @media(min-width:992px){
    //     display:none;
    //   }
    // }
    .cart-list{
      border-top:none;
      .cart-item-title{
        @media(min-width:768px){
          width:40%;
          padding-left:20px;
          @media(max-width:1100px){
            padding:20px 0;
            padding-left:20px;
          }
        }
      }
    }
    @media(max-width:1100px){
      width:72%;
    }
    @media(max-width:991px){
      width:65%;
    }
    @media(max-width:800px){
      width:100%;
      .cart-item-price-label{
        display:none;
      }
      .cart-item-value{
        padding-left:0;
      }
    }
  }
  .row{
    width:30%;
    float:right;
    margin:0;
    .cart-total-div{
      width:100%;
      border-left: 1px solid #e8e8e8;
      padding-left:20px;
      .cart-actions{
        width:100%;
        padding-left:0;
      }
      .cart-total{
        margin-right:0;
      }
    }
    .cart-grandtotal-label{
      @media(min-width:768px){
        width:62%;
      }
    }
    @media(max-width:1100px){
      width:25%;
      padding-right:16px;
    }
    @media(max-width:991px){
      width:33%;
    }
    @media(max-width:800px){
      width:100%;
    }
  }
  .cart-additionalCheckoutButtons{
    width:100%;
    float:right;
  }
  .cart-actions{
    margin-right:0;
  }
}

.cart-page-title{
  display:inline-block;
  width:100%;
  .page-heading{
    width:25%;
    display:inline-block;
    float:left;
    @media(max-width:1199px){
      width:20%;
    }
    @media(max-width:800px){
      width:100%;
    }
  }
  .value-divs{
    float:right;
    display:inline-block;
    @media(max-width:958px){
      width:100%;
    }
  }
  .shipping-value{
    min-width:428px;
    float:right;
    @media(max-width:1060px){
      min-width:400px;
    }
    @media(max-width:958px){
      float:left;
    }
    @media(max-width:800px){
      width:100%;
      min-width:1px;
      .value-icon{
        img{
          padding-right:5px;
        }
      }
    }
  }
  .returns-value{
    min-width:348px;
    float:right;
    .value-text{
      padding-left:0;
    }
    @media(max-width:1060px){
      min-width:322px;
    }
    @media(max-width:800px){
      width:100%;
      min-width:1px;
      margin-bottom:10px;
    }
  }
  .value-icon{
    width:50px;
    float:left;
    img{
      max-width: 60px;
      float: left;
      display: inline-block;
      margin-bottom: 3px;
      height: auto;
      width: auto;
      @media(max-width:330px){
        max-width:58px;
      }
    }
    @media(max-width:800px){
      width:auto;
    }
  }
  .value-text{
    float:right;
    @media(max-width:800px){
      float:left;
      width:80%;
    }
    @media(max-width:360px){
      width:78%;
    }
    h2{
      margin-top:0;
      font-style:italic;
      font-weight:500;
      font-size:14px;
      width:100%;
      margin-bottom:0px;
      text-transform:uppercase;
      @media(max-width:360px){
        width:auto;
      }
    }
    p{
      margin-bottom:0;
      width:100%;
      font-size:13px;
      @media(max-width:1060px){
        font-size:12px;
      }
      @media(max-width:360px){
        width:auto;
      }
    }
  }
}

.added-breadcrumb{
  a{
    border-bottom:1px solid transparent;
  }
  .material-icons{
    color: #cdcdcf;
    margin: 0;
    font-size:19px;
    vertical-align:bottom;
  }
}
