components/amp/css/product:
.productView {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 1rem 1.5rem;
    margin-top: 1.5rem;
}

.productView-images {
    padding: 2rem 0;
}

.productView-action {
    margin-bottom: 2rem;
}

.productAccordion-title {
    background-color: transparent;
    border-right: 0;
    border-left: 0;
    padding: .75rem 0;
    text-align: center;
    margin-bottom: -1px;
    font-size: 1rem;
}
.productAccordion-content {
    padding: 1rem 0;
}

.socialLinks {
    margin: 1rem 0 0;
    padding: 0;
    text-align: center;
}

.socialLinks-item {
    margin: 0;
    padding: 0;
    display: inline-block;
    font-size: 0;
    margin-bottom: .35714rem;
    margin-right: .78571rem
}
.socialLinks-item:last-child {
    margin-right: 0
}


.writeReview-productDetails .product-brand {
    color: #a5a5a5;
    margin: 0
}
.writeReview-productDetails .product-title {
    margin-top: 0
}
.account--addReturn .table {
    display: block;
    margin-bottom: 3rem
}
.quickView:before,
.quickView:after {
    content: " ";
    display: table
}
.quickView:after {
    clear: both
}
.productReviews-list {
    list-style: none;
    margin: 0 -.75rem;
    max-width: none;
    width: auto;
    font-size: 0
}
.productReviews-list ul,
.productReviews-list ol {
    list-style: none;
    margin-bottom: 0
}
.productReviews-list:before,
.productReviews-list:after {
    content: " ";
    display: table
}
.productReviews-list:after {
    clear: both
}
.productReview {
    padding-left: .75rem;
    padding-right: .75rem;
    width: 100%;
    float: left;
    display: inline-block;
    float: none;
    font-size: 1rem;
    vertical-align: top
}
.productReview-title {
    margin: .5rem 0;
    text-transform: none
}
.productReview-author {
    color: #989898;
    margin: .5rem 0 1rem
}
.productReview-rating {
    display: inline-block;
    margin-bottom: .35714rem
}
.productReview-ratingNumber {
    border: 0;
    clip: rect(1px, 1px, 1px, 1px);
    height: 1px;
    overflow: hidden;
    padding: 0;
    position: absolute;
    width: 1px
}
.productView-details {
    margin-bottom: 2rem
}
.productView-product >:last-child {
    margin-bottom: 0
}
.productView-title {
    font-size: 20px;
    margin: -.78571rem 0 .35714rem
}
.productView-brand {
    color: #989898;
    font-size: 15px;
    margin: 0 0 .78571rem
}
.productView-brand a {
    color: #989898
}
.productView-brand a:hover {
    color: #4f4f4f
}
.productView-price {
    font-size: 15px;
    margin-bottom: .5rem
}
.productView-price .price-section--saving {
    font-size: 15px
}
.productView-price abbr {
    border: 0;
    color: inherit;
    font-size: inherit
}
.productView-info {
    margin-top: .78571rem
}
.productView-info:before,
.productView-info:after {
    content: " ";
    display: table
}
.productView-info >:last-child {
    margin-bottom: 0
}
.productView-info-name,
.productView-info-value {
    float: left
}
.productView-info-name {
    clear: both;
    margin-bottom: .21429rem;
    margin-right: .35714rem
}
.productView-info-value {
    margin-bottom: .35714rem;
    margin-left: 0;
}
